-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: ESPALDA
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('40000000-0001-0000-0000-000000000001', 'es', 'Dominadas (Pull-ups)', 'dominadas pull ups chin ups',
'["Colgá de una barra con agarre pronado (palmas hacia adelante) ligeramente más ancho que los hombros.", "Mantené el core activado y las piernas juntas o ligeramente cruzadas.", "Tirate hacia arriba llevando el pecho hacia la barra hasta que la barbilla supere la barra.", "Descendé de forma controlada hasta extender completamente los brazos.", "Evitá balancearte o usar impulso para subir."]'::jsonb),

('40000000-0001-0000-0000-000000000002', 'es', 'Dominadas supinas (Chin-ups)', 'dominadas supinas chin ups',
'["Colgá de una barra con agarre supino (palmas hacia vos) al ancho de los hombros.", "Mantené el core firme y las piernas controladas.", "Tirate hacia arriba llevando el mentón por encima de la barra.", "Bajá de forma controlada hasta la extensión completa de los brazos.", "Este agarre enfatiza más los bíceps junto con los dorsales."]'::jsonb),

('40000000-0001-0000-0000-000000000003', 'es', 'Remo con barra', 'remo con barra bent over row',
'["Parate con los pies al ancho de los hombros, rodillas ligeramente flexionadas.", "Incliná el torso hacia adelante manteniendo la espalda recta y el core firme.", "Sostené la barra con agarre pronado, brazos extendidos.", "Tirá la barra hacia el abdomen bajo, llevando los codos hacia atrás.", "Bajá la barra de forma controlada hasta la posición inicial."]'::jsonb),

('40000000-0001-0000-0000-000000000004', 'es', 'Remo con mancuerna a una mano', 'remo con mancuerna a una mano single arm dumbbell row',
'["Apoyá una mano y una rodilla en un banco, manteniendo la espalda recta.", "Sostené una mancuerna con la otra mano, brazo extendido hacia el suelo.", "Tirá la mancuerna hacia la cadera llevando el codo hacia arriba y atrás.", "Bajá de forma controlada hasta extender el brazo completamente.", "Evitá rotar el torso durante el movimiento."]'::jsonb),

('40000000-0001-0000-0000-000000000005', 'es', 'Jalón al pecho en polea alta', 'jalon al pecho en polea alta lat pulldown',
'["Sentate en la máquina de jalón con los muslos bien sujetos bajo las almohadillas.", "Tomá la barra con agarre pronado más ancho que los hombros.", "Tirá la barra hacia el pecho superior, llevando los codos hacia abajo y atrás.", "Contraé los dorsales en la parte baja del movimiento.", "Dejá que la barra suba de forma controlada sin perder tensión."]'::jsonb),

('40000000-0001-0000-0000-000000000006', 'es', 'Remo en máquina', 'remo en maquina seated row machine',
'["Sentate en la máquina con el pecho apoyado en el soporte y los pies firmes.", "Tomá las empuñaduras con agarre neutro o pronado según la máquina.", "Tirá las empuñaduras hacia el torso, retrayendo los omóplatos.", "Mantené un segundo la contracción y volvé de forma controlada a la posición inicial.", "Evitá inclinar el torso hacia atrás durante el tirón."]'::jsonb),

('40000000-0001-0000-0000-000000000007', 'es', 'Remo en polea baja', 'remo en polea baja seated cable row',
'["Sentate frente a la polea baja con los pies apoyados en la plataforma y las rodillas ligeramente flexionadas.", "Tomá el agarre en V o la barra con los brazos extendidos.", "Tirá el agarre hacia el abdomen, retrayendo los omóplatos y manteniendo el torso erguido.", "Volvé de forma controlada extendiendo los brazos sin redondear la espalda.", "Mantené el pecho abierto durante todo el movimiento."]'::jsonb),

('40000000-0001-0000-0000-000000000008', 'es', 'Pullover con mancuerna', 'pullover con mancuerna dumbbell',
'["Acostate transversalmente sobre un banco con solo los hombros apoyados.", "Sostené una mancuerna con ambas manos por encima del pecho, brazos ligeramente flexionados.", "Bajá la mancuerna hacia atrás por encima de la cabeza manteniendo los codos fijos.", "Volvé a la posición inicial contrayendo los dorsales.", "Mantené las caderas bajas y el core firme durante todo el movimiento."]'::jsonb),

('40000000-0001-0000-0000-000000000009', 'es', 'Remo Pendlay', 'remo pendlay barra',
'["Parate con los pies al ancho de los hombros, la barra en el suelo frente a vos.", "Incliná el torso hasta que esté paralelo al suelo, espalda recta.", "Tomá la barra con agarre pronado y tirala explosivamente hacia el pecho/abdomen.", "Bajá la barra hasta el suelo entre cada repetición.", "Mantené el core firme y evitá usar impulso de piernas."]'::jsonb),

('40000000-0001-0000-0000-000000000010', 'es', 'Peso muerto convencional', 'peso muerto convencional deadlift',
'["Parate con los pies al ancho de las caderas, la barra sobre el empeine.", "Flexioná las caderas y rodillas, tomá la barra con agarre mixto o doble pronado.", "Mantené la espalda recta, pecho arriba y core firme.", "Extendé las caderas y rodillas simultáneamente para levantar la barra.", "Bajá de forma controlada revirtiendo el movimiento.", "La barra debe permanecer cerca del cuerpo durante todo el levantamiento."]'::jsonb),

('40000000-0001-0000-0000-000000000011', 'es', 'Jalón con agarre neutro', 'jalon con agarre neutro close grip pulldown',
'["Sentate en la máquina de jalón y conectá un agarre en V o con mangos paralelos.", "Tomá el agarre con las palmas enfrentadas.", "Tirá el agarre hacia el pecho, llevando los codos hacia abajo y atrás.", "Contraé los dorsales y mantené un segundo.", "Dejá que el agarre suba de forma controlada."]'::jsonb),

('40000000-0001-0000-0000-000000000012', 'es', 'Remo con barra T', 'remo con barra t t bar row',
'["Colocá un extremo de la barra en una esquina o soporte de barra T.", "Parate sobre la barra con los pies al ancho de los hombros.", "Incliná el torso y tomá la barra con agarre neutro usando un accesorio en V.", "Tirá la barra hacia el pecho manteniendo los codos cerca del cuerpo.", "Bajá de forma controlada sin redondear la espalda."]'::jsonb),

('40000000-0001-0000-0000-000000000013', 'es', 'Peso muerto rumano', 'peso muerto rumano romanian deadlift rdl',
'["Parate con los pies al ancho de las caderas sosteniendo la barra frente a los muslos.", "Mantené las rodillas ligeramente flexionadas y fijas durante el movimiento.", "Flexioná las caderas llevando la barra hacia abajo por delante de las piernas.", "Bajá hasta sentir un estiramiento en los isquiotibiales sin redondear la espalda.", "Volvé a la posición inicial extendiendo las caderas y contrayendo los glúteos."]'::jsonb),

('40000000-0001-0000-0000-000000000014', 'es', 'Hiperextensiones', 'hiperextensiones back extension',
'["Posicionate en el banco de hiperextensiones con las caderas en el borde de la almohadilla.", "Cruzá los brazos sobre el pecho o colocalos detrás de la cabeza.", "Bajá el torso hacia el suelo flexionando las caderas.", "Levantá el torso hasta alinearlo con las piernas, contrayendo los erectores.", "Evitá hiperextender la espalda más allá de la posición neutra."]'::jsonb),

('40000000-0001-0000-0000-000000000015', 'es', 'Remo invertido', 'remo invertido inverted row body weight',
'["Colocá una barra a la altura de la cadera en un rack o usá un TRX.", "Colgá debajo de la barra con agarre pronado o supino, cuerpo en línea recta.", "Tirá el pecho hacia la barra llevando los codos hacia atrás.", "Bajá de forma controlada hasta extender los brazos completamente.", "Mantené el core firme y las caderas alineadas durante todo el movimiento."]'::jsonb),

('40000000-0001-0000-0000-000000000016', 'es', 'Remo con mancuernas a dos manos', 'remo con mancuernas a dos manos bent over dumbbell row',
'["Parate con los pies al ancho de los hombros, una mancuerna en cada mano.", "Incliná el torso hacia adelante manteniendo la espalda recta.", "Tirá ambas mancuernas hacia las caderas llevando los codos hacia atrás.", "Bajá de forma controlada hasta extender los brazos.", "Evitá balancearte o usar impulso."]'::jsonb),

('40000000-0001-0000-0000-000000000017', 'es', 'Encogimientos de hombros con barra', 'encogimientos de hombros con barra barbell shrugs',
'["Parate sosteniendo una barra frente a los muslos con los brazos extendidos.", "Elevá los hombros hacia las orejas contrayendo los trapecios.", "Mantené la contracción un segundo en la parte alta.", "Bajá de forma controlada a la posición inicial.", "Evitá rotar los hombros o flexionar los codos."]'::jsonb),

('40000000-0001-0000-0000-000000000018', 'es', 'Encogimientos de hombros con mancuernas', 'encogimientos de hombros con mancuernas dumbbell shrugs',
'["Parate con una mancuerna en cada mano a los costados del cuerpo.", "Elevá los hombros hacia las orejas manteniendo los brazos rectos.", "Contraé los trapecios en la parte alta del movimiento.", "Bajá de forma controlada sin dejar caer el peso.", "Mantené la cabeza en posición neutra."]'::jsonb),

('40000000-0001-0000-0000-000000000019', 'es', 'Jalón tras nuca', 'jalon tras nuca behind neck pulldown',
'["Sentate en la máquina de jalón con agarre ancho.", "Tirá la barra hacia la nuca manteniendo el torso erguido.", "Llevá los codos hacia abajo y hacia atrás.", "Dejá que la barra suba de forma controlada.", "Usá un peso moderado para evitar tensión excesiva en los hombros."]'::jsonb),

('40000000-0001-0000-0000-000000000020', 'es', 'Remo con agarre supino', 'remo con agarre supino underhand row',
'["Parate con los pies al ancho de los hombros, rodillas ligeramente flexionadas.", "Tomá la barra con agarre supino (palmas hacia arriba).", "Incliná el torso hacia adelante y tirá la barra hacia el abdomen.", "Bajá de forma controlada sin redondear la espalda.", "Este agarre enfatiza más los bíceps y el dorsal inferior."]'::jsonb),

('40000000-0001-0000-0000-000000000021', 'es', 'Pulldown con brazos rectos', 'pulldown con brazos rectos straight arm lat pulldown',
'["Parate frente a la polea alta con una barra recta o cuerda.", "Tomá el accesorio con los brazos extendidos a la altura del pecho.", "Con los codos ligeramente flexionados, bajá la barra hacia los muslos.", "Contraé los dorsales al final del movimiento.", "Volvé de forma controlada a la posición inicial."]'::jsonb),

('40000000-0001-0000-0000-000000000022', 'es', 'Good morning', 'good morning barra',
'["Colocá una barra sobre los trapecios como en una sentadilla.", "Parate con los pies al ancho de los hombros, rodillas ligeramente flexionadas.", "Flexioná las caderas llevando el torso hacia adelante manteniendo la espalda recta.", "Bajá hasta que el torso quede casi paralelo al suelo.", "Volvé a la posición inicial extendiendo las caderas."]'::jsonb),

('40000000-0001-0000-0000-000000000023', 'es', 'Remo Seal', 'remo seal con mancuernas prone row',
'["Acostate boca abajo sobre un banco elevado sosteniendo una mancuerna en cada mano.", "Dejá que los brazos cuelguen hacia el suelo.", "Tirá las mancuernas hacia las caderas llevando los codos hacia atrás.", "Contraé los dorsales en la parte alta y bajá de forma controlada.", "Este ejercicio elimina el impulso del torso."]'::jsonb),

('40000000-0001-0000-0000-000000000024', 'es', 'Dominadas con agarre neutro', 'dominadas con agarre neutro neutral grip pull ups',
'["Colgá de barras paralelas con agarre neutro (palmas enfrentadas).", "Mantené el core activado y las piernas controladas.", "Tirate hacia arriba llevando el pecho hacia las barras.", "Bajá de forma controlada hasta la extensión completa de los brazos.", "Este agarre es más amigable para los hombros y muñecas."]'::jsonb),

('40000000-0001-0000-0000-000000000025', 'es', 'Remo Meadows', 'remo meadows landmine row',
'["Colocá un extremo de la barra en una esquina y cargá el otro con discos.", "Parate perpendicular a la barra e incliná el torso.", "Tomá el extremo cargado con una mano y tirá hacia la cadera.", "Bajá de forma controlada sin rotar el torso.", "Este remo unilateral permite mayor rango de movimiento."]'::jsonb),

('40000000-0001-0000-0000-000000000026', 'es', 'Peso muerto sumo', 'peso muerto sumo sumo deadlift',
'["Parate con los pies muy separados, puntas hacia afuera, la barra sobre los empeines.", "Flexioná caderas y rodillas, tomá la barra con agarre cerrado entre las piernas.", "Mantené el pecho arriba y la espalda recta.", "Extendé caderas y rodillas para levantar la barra.", "Este estilo enfatiza más los cuádriceps y aductores."]'::jsonb),

('40000000-0001-0000-0000-000000000027', 'es', 'Remo con banda elástica', 'remo con banda elastica resistance band row',
'["Sentate en el suelo con las piernas extendidas y la banda alrededor de los pies.", "Tomá los extremos de la banda con ambas manos.", "Tirá la banda hacia el abdomen, retrayendo los omóplatos.", "Volvé de forma controlada extendiendo los brazos.", "Mantené el torso erguido durante todo el movimiento."]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('40000000-0001-0000-0000-000000000001', 'en', 'Pull-Ups', 'pull ups chin ups',
'["Hang from bar with overhand grip (palms forward) slightly wider than shoulders.", "Keep core engaged and legs together or slightly crossed.", "Pull yourself up bringing chest toward bar until chin clears bar.", "Lower in controlled manner until arms are fully extended.", "Avoid swinging or using momentum to go up."]'::jsonb),

('40000000-0001-0000-0000-000000000002', 'en', 'Chin-Ups', 'chin ups supinated pull ups',
'["Hang from bar with underhand grip (palms toward you) at shoulder width.", "Keep core tight and legs controlled.", "Pull yourself up bringing chin over bar.", "Lower in controlled manner to full arm extension.", "This grip emphasizes biceps more along with lats."]'::jsonb),

('40000000-0001-0000-0000-000000000003', 'en', 'Bent Over Barbell Row', 'bent over barbell row',
'["Stand with feet shoulder-width apart, knees slightly bent.", "Lean torso forward keeping back straight and core tight.", "Hold bar with overhand grip, arms extended.", "Pull bar toward lower abdomen, bringing elbows back.", "Lower bar in controlled manner to starting position."]'::jsonb),

('40000000-0001-0000-0000-000000000004', 'en', 'Single Arm Dumbbell Row', 'single arm dumbbell row one arm',
'["Place one hand and knee on bench, keeping back straight.", "Hold dumbbell with other hand, arm extended toward floor.", "Pull dumbbell toward hip bringing elbow up and back.", "Lower in controlled manner until arm is fully extended.", "Avoid rotating torso during movement."]'::jsonb),

('40000000-0001-0000-0000-000000000005', 'en', 'Lat Pulldown', 'lat pulldown cable',
'["Sit at pulldown machine with thighs secured under pads.", "Grip bar with overhand grip wider than shoulders.", "Pull bar toward upper chest, bringing elbows down and back.", "Contract lats at bottom of movement.", "Let bar rise in controlled manner without losing tension."]'::jsonb),

('40000000-0001-0000-0000-000000000006', 'en', 'Machine Row', 'machine row seated',
'["Sit at machine with chest against support and feet firm.", "Grip handles with neutral or overhand grip depending on machine.", "Pull handles toward torso, retracting shoulder blades.", "Hold contraction for a second and return in controlled manner to starting position.", "Avoid leaning torso back during pull."]'::jsonb),

('40000000-0001-0000-0000-000000000007', 'en', 'Seated Cable Row', 'seated cable row low pulley',
'["Sit facing low pulley with feet on platform and knees slightly bent.", "Grip V-handle or bar with arms extended.", "Pull handle toward abdomen, retracting shoulder blades and keeping torso upright.", "Return in controlled manner extending arms without rounding back.", "Keep chest open throughout movement."]'::jsonb),

('40000000-0001-0000-0000-000000000008', 'en', 'Dumbbell Pullover', 'dumbbell pullover',
'["Lie across bench with only shoulders supported.", "Hold dumbbell with both hands above chest, arms slightly bent.", "Lower dumbbell back over head keeping elbows fixed.", "Return to starting position contracting lats.", "Keep hips low and core tight throughout movement."]'::jsonb),

('40000000-0001-0000-0000-000000000009', 'en', 'Pendlay Row', 'pendlay row barbell',
'["Stand with feet shoulder-width apart, bar on floor in front of you.", "Lean torso until parallel to floor, back straight.", "Grip bar with overhand grip and pull explosively toward chest/abdomen.", "Lower bar to floor between each rep.", "Keep core tight and avoid using leg drive."]'::jsonb),

('40000000-0001-0000-0000-000000000010', 'en', 'Conventional Deadlift', 'conventional deadlift',
'["Stand with feet hip-width apart, bar over midfoot.", "Bend hips and knees, grip bar with mixed or double overhand grip.", "Keep back straight, chest up and core tight.", "Extend hips and knees simultaneously to lift bar.", "Lower in controlled manner reversing movement.", "Bar should stay close to body throughout lift."]'::jsonb),

('40000000-0001-0000-0000-000000000011', 'en', 'Close Grip Pulldown', 'close grip pulldown neutral grip',
'["Sit at pulldown machine and attach V-handle or parallel grip handles.", "Grip handles with palms facing each other.", "Pull handle toward chest, bringing elbows down and back.", "Contract lats and hold for a second.", "Let handle rise in controlled manner."]'::jsonb),

('40000000-0001-0000-0000-000000000012', 'en', 'T-Bar Row', 't bar row landmine',
'["Place one end of bar in corner or T-bar support.", "Stand over bar with feet shoulder-width apart.", "Lean torso and grip bar with neutral grip using V-handle.", "Pull bar toward chest keeping elbows close to body.", "Lower in controlled manner without rounding back."]'::jsonb),

('40000000-0001-0000-0000-000000000013', 'en', 'Romanian Deadlift', 'romanian deadlift rdl stiff leg',
'["Stand with feet hip-width apart holding bar in front of thighs.", "Keep knees slightly bent and fixed throughout movement.", "Hinge at hips lowering bar down front of legs.", "Lower until you feel hamstring stretch without rounding back.", "Return to starting position extending hips and squeezing glutes."]'::jsonb),

('40000000-0001-0000-0000-000000000014', 'en', 'Back Extensions', 'back extensions hyperextensions',
'["Position yourself on hyperextension bench with hips at pad edge.", "Cross arms over chest or place behind head.", "Lower torso toward floor by bending at hips.", "Raise torso until aligned with legs, contracting erectors.", "Avoid hyperextending back beyond neutral position."]'::jsonb),

('40000000-0001-0000-0000-000000000015', 'en', 'Inverted Row', 'inverted row bodyweight australian',
'["Set bar at hip height on rack or use TRX.", "Hang under bar with overhand or underhand grip, body in straight line.", "Pull chest toward bar bringing elbows back.", "Lower in controlled manner until arms fully extended.", "Keep core tight and hips aligned throughout movement."]'::jsonb),

('40000000-0001-0000-0000-000000000016', 'en', 'Two Arm Dumbbell Bent Over Row', 'two arm dumbbell bent over row',
'["Stand with feet shoulder-width apart, dumbbell in each hand.", "Lean torso forward keeping back straight.", "Pull both dumbbells toward hips bringing elbows back.", "Lower in controlled manner until arms extended.", "Avoid swinging or using momentum."]'::jsonb),

('40000000-0001-0000-0000-000000000017', 'en', 'Barbell Shrugs', 'barbell shrugs trapezius',
'["Stand holding bar in front of thighs with arms extended.", "Raise shoulders toward ears contracting traps.", "Hold contraction for a second at top.", "Lower in controlled manner to starting position.", "Avoid rotating shoulders or bending elbows."]'::jsonb),

('40000000-0001-0000-0000-000000000018', 'en', 'Dumbbell Shrugs', 'dumbbell shrugs trapezius',
'["Stand with dumbbell in each hand at sides.", "Raise shoulders toward ears keeping arms straight.", "Contract traps at top of movement.", "Lower in controlled manner without dropping weight.", "Keep head in neutral position."]'::jsonb),

('40000000-0001-0000-0000-000000000019', 'en', 'Behind Neck Pulldown', 'behind neck pulldown lat',
'["Sit at pulldown machine with wide grip.", "Pull bar toward back of neck keeping torso upright.", "Bring elbows down and back.", "Let bar rise in controlled manner.", "Use moderate weight to avoid excessive shoulder strain."]'::jsonb),

('40000000-0001-0000-0000-000000000020', 'en', 'Underhand Barbell Row', 'underhand barbell row supinated yates',
'["Stand with feet shoulder-width apart, knees slightly bent.", "Grip bar with underhand grip (palms up).", "Lean torso forward and pull bar toward abdomen.", "Lower in controlled manner without rounding back.", "This grip emphasizes biceps and lower lats more."]'::jsonb),

('40000000-0001-0000-0000-000000000021', 'en', 'Straight Arm Lat Pulldown', 'straight arm lat pulldown cable',
'["Stand facing high pulley with straight bar or rope.", "Grip attachment with arms extended at chest height.", "With elbows slightly bent, pull bar down toward thighs.", "Contract lats at end of movement.", "Return in controlled manner to starting position."]'::jsonb),

('40000000-0001-0000-0000-000000000022', 'en', 'Good Morning', 'good morning barbell',
'["Place bar on traps as in a squat.", "Stand with feet shoulder-width apart, knees slightly bent.", "Hinge at hips bringing torso forward keeping back straight.", "Lower until torso is nearly parallel to floor.", "Return to starting position by extending hips."]'::jsonb),

('40000000-0001-0000-0000-000000000023', 'en', 'Seal Row', 'seal row dumbbell prone',
'["Lie face down on elevated bench holding dumbbell in each hand.", "Let arms hang toward floor.", "Pull dumbbells toward hips bringing elbows back.", "Contract lats at top and lower in controlled manner.", "This exercise eliminates torso momentum."]'::jsonb),

('40000000-0001-0000-0000-000000000024', 'en', 'Neutral Grip Pull-Ups', 'neutral grip pull ups parallel',
'["Hang from parallel bars with neutral grip (palms facing).", "Keep core engaged and legs controlled.", "Pull yourself up bringing chest toward bars.", "Lower in controlled manner to full arm extension.", "This grip is easier on shoulders and wrists."]'::jsonb),

('40000000-0001-0000-0000-000000000025', 'en', 'Meadows Row', 'meadows row landmine single arm',
'["Place one end of bar in corner and load other end with plates.", "Stand perpendicular to bar and lean torso.", "Grip loaded end with one hand and pull toward hip.", "Lower in controlled manner without rotating torso.", "This unilateral row allows greater range of motion."]'::jsonb),

('40000000-0001-0000-0000-000000000026', 'en', 'Sumo Deadlift', 'sumo deadlift wide stance',
'["Stand with feet wide apart, toes pointed out, bar over midfoot.", "Bend hips and knees, grip bar with narrow grip between legs.", "Keep chest up and back straight.", "Extend hips and knees to lift bar.", "This style emphasizes quads and adductors more."]'::jsonb),

('40000000-0001-0000-0000-000000000027', 'en', 'Resistance Band Row', 'resistance band row seated',
'["Sit on floor with legs extended and band around feet.", "Grip band ends with both hands.", "Pull band toward abdomen, retracting shoulder blades.", "Return in controlled manner extending arms.", "Keep torso upright throughout movement."]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '40000000-0001-%'
GROUP BY language_code;
