-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: BÍCEPS
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('50000000-0001-0000-0000-000000000001', 'es', 'Curl con barra recta', 'curl con barra recta barbell',
'["Parate con los pies al ancho de los hombros, sosteniendo la barra con agarre supino (palmas hacia arriba).", "Mantené los codos pegados al cuerpo y el core activado.", "Flexioná los codos para levantar la barra hacia los hombros sin mover los brazos superiores.", "Contraé los bíceps en la parte alta del movimiento.", "Bajá la barra de forma controlada hasta la extensión completa."]'::jsonb),

('50000000-0001-0000-0000-000000000002', 'es', 'Curl con barra EZ', 'curl con barra ez',
'["Tomá la barra EZ por las partes anguladas con agarre supino.", "Parate erguido con los codos cerca del cuerpo.", "Flexioná los codos para subir la barra hacia los hombros.", "Mantené los brazos superiores fijos durante todo el movimiento.", "Bajá de forma controlada hasta extender completamente los brazos."]'::jsonb),

('50000000-0001-0000-0000-000000000003', 'es', 'Curl con mancuernas alterno', 'curl con mancuernas alterno alternating dumbbell',
'["Parate con una mancuerna en cada mano, brazos a los costados, palmas hacia el cuerpo.", "Flexioná un codo y girá la muñeca durante el ascenso hasta que la palma mire hacia arriba.", "Subí la mancuerna hacia el hombro contrayendo el bíceps.", "Bajá de forma controlada mientras repetís con el otro brazo.", "Evitá balancear el cuerpo o usar impulso."]'::jsonb),

('50000000-0001-0000-0000-000000000004', 'es', 'Curl martillo', 'curl martillo hammer',
'["Parate con una mancuerna en cada mano, palmas enfrentadas (agarre neutro).", "Mantené los codos cerca del cuerpo.", "Flexioná los codos para subir las mancuernas hacia los hombros sin girar las muñecas.", "Contraé en la parte alta y bajá de forma controlada.", "Este agarre trabaja más el braquial y el braquiorradial."]'::jsonb),

('50000000-0001-0000-0000-000000000005', 'es', 'Curl concentrado', 'curl concentrado concentration',
'["Sentate en un banco con las piernas separadas, una mancuerna en una mano.", "Apoyá el codo contra la cara interna del muslo.", "Flexioná el codo para subir la mancuerna hacia el hombro.", "Contraé fuertemente el bíceps en la parte alta.", "Bajá de forma controlada y repetí. Cambiá de brazo al terminar."]'::jsonb),

('50000000-0001-0000-0000-000000000006', 'es', 'Curl en banco inclinado', 'curl en banco inclinado incline dumbbell',
'["Acostate en un banco inclinado (45 grados) con una mancuerna en cada mano, brazos colgando.", "Mantené los codos fijos mientras flexionás para subir las mancuernas.", "Contraé los bíceps en la parte alta del movimiento.", "Bajá de forma controlada hasta la extensión completa.", "La inclinación aumenta el estiramiento del bíceps."]'::jsonb),

('50000000-0001-0000-0000-000000000007', 'es', 'Curl en polea baja', 'curl en polea baja cable',
'["Parate frente a la polea baja con una barra o agarre conectado.", "Tomá el agarre con las palmas hacia arriba.", "Flexioná los codos para subir el agarre hacia los hombros.", "Mantené los codos fijos al costado del cuerpo.", "Bajá de forma controlada manteniendo tensión constante."]'::jsonb),

('50000000-0001-0000-0000-000000000008', 'es', 'Curl predicador con barra', 'curl predicador con barra preacher',
'["Sentate en el banco predicador con los brazos apoyados sobre la almohadilla.", "Tomá la barra con agarre supino.", "Flexioná los codos para subir la barra hacia los hombros.", "Contraé los bíceps sin despegar los brazos de la almohadilla.", "Bajá de forma controlada hasta casi la extensión completa."]'::jsonb),

('50000000-0001-0000-0000-000000000009', 'es', 'Curl predicador con mancuerna', 'curl predicador con mancuerna preacher dumbbell',
'["Sentate en el banco predicador con un brazo apoyado sobre la almohadilla.", "Sostené una mancuerna con agarre supino.", "Flexioná el codo para subir la mancuerna hacia el hombro.", "Bajá de forma controlada sin extender completamente el codo.", "Completá las repeticiones y cambiá de brazo."]'::jsonb),

('50000000-0001-0000-0000-000000000010', 'es', 'Curl araña', 'curl arana spider',
'["Acostate boca abajo sobre un banco inclinado con los brazos colgando.", "Sostené mancuernas o una barra con agarre supino.", "Flexioná los codos para subir el peso hacia los hombros.", "Contraé fuertemente los bíceps en la parte alta.", "Bajá de forma controlada. La gravedad aumenta la tensión."]'::jsonb),

('50000000-0001-0000-0000-000000000011', 'es', 'Curl 21s', 'curl 21s veintiuno',
'["Realizá 7 repeticiones parciales desde abajo hasta la mitad del recorrido.", "Luego, 7 repeticiones parciales desde la mitad hasta arriba.", "Finalmente, 7 repeticiones completas con rango completo.", "Mantené los codos fijos durante todo el set.", "Este método aumenta el tiempo bajo tensión."]'::jsonb),

('50000000-0001-0000-0000-000000000012', 'es', 'Curl en máquina', 'curl en maquina machine bicep',
'["Ajustá el asiento para que los codos queden alineados con el eje de la máquina.", "Tomá las empuñaduras con agarre supino.", "Flexioná los codos para subir las empuñaduras hacia los hombros.", "Contraé los bíceps y bajá de forma controlada.", "La máquina proporciona tensión constante durante todo el movimiento."]'::jsonb),

('50000000-0001-0000-0000-000000000013', 'es', 'Curl con agarre inverso', 'curl con agarre inverso reverse grip',
'["Tomá una barra con agarre pronado (palmas hacia abajo).", "Parate erguido con los codos cerca del cuerpo.", "Flexioná los codos para subir la barra hacia los hombros.", "Bajá de forma controlada.", "Este agarre enfatiza el braquiorradial y los extensores del antebrazo."]'::jsonb),

('50000000-0001-0000-0000-000000000014', 'es', 'Curl de muñeca', 'curl de muneca wrist forearm',
'["Sentate con los antebrazos apoyados sobre los muslos, muñecas fuera de las rodillas.", "Sostené una barra o mancuernas con agarre supino.", "Flexioná las muñecas para subir el peso.", "Bajá de forma controlada extendiendo las muñecas.", "Este ejercicio trabaja los flexores del antebrazo."]'::jsonb),

('50000000-0001-0000-0000-000000000015', 'es', 'Curl de muñeca inverso', 'curl de muneca inverso reverse wrist',
'["Sentate con los antebrazos apoyados sobre los muslos, muñecas fuera de las rodillas.", "Sostené una barra o mancuernas con agarre pronado.", "Extendé las muñecas para subir el peso.", "Bajá de forma controlada flexionando las muñecas.", "Este ejercicio trabaja los extensores del antebrazo."]'::jsonb),

('50000000-0001-0000-0000-000000000016', 'es', 'Curl cruzado (Cross-body)', 'curl cruzado cross body hammer',
'["Parate con una mancuerna en cada mano a los costados.", "Flexioná un codo y llevá la mancuerna hacia el hombro opuesto cruzando el cuerpo.", "Mantené el codo cerca del torso durante el movimiento.", "Bajá de forma controlada y repetí con el otro brazo.", "Este ejercicio enfatiza el braquial."]'::jsonb),

('50000000-0001-0000-0000-000000000017', 'es', 'Curl en polea alta (overhead)', 'curl en polea alta overhead cable',
'["Parate entre dos poleas altas con los cables a la altura de los hombros.", "Tomá las empuñaduras con agarre supino, brazos extendidos a los lados.", "Flexioná los codos llevando las manos hacia la cabeza.", "Contraé los bíceps y volvé de forma controlada a la posición inicial.", "Mantené los brazos superiores paralelos al suelo."]'::jsonb),

('50000000-0001-0000-0000-000000000018', 'es', 'Curl con banda elástica', 'curl con banda elastica resistance band',
'["Pisá la banda con ambos pies y tomá los extremos con las manos.", "Mantené los codos cerca del cuerpo.", "Flexioná los codos para subir las manos hacia los hombros.", "Contraé los bíceps y bajá de forma controlada.", "La resistencia aumenta a medida que estirás la banda."]'::jsonb),

('50000000-0001-0000-0000-000000000019', 'es', 'Curl Zottman', 'curl zottman mancuernas',
'["Parate con una mancuerna en cada mano, palmas hacia arriba.", "Flexioná los codos para subir las mancuernas hacia los hombros.", "En la parte alta, girá las muñecas hasta que las palmas miren hacia abajo.", "Bajá las mancuernas con este agarre pronado.", "Girá las muñecas nuevamente al llegar abajo para la siguiente repetición."]'::jsonb),

('50000000-0001-0000-0000-000000000020', 'es', 'Curl de arrastre (Drag curl)', 'curl de arrastre drag curl',
'["Parate sosteniendo una barra con agarre supino frente a los muslos.", "En lugar de curvar hacia adelante, llevá los codos hacia atrás mientras subís la barra.", "La barra debe \"arrastrarse\" pegada al cuerpo.", "Subí hasta que la barra llegue al pecho.", "Bajá de forma controlada arrastrando la barra."]'::jsonb),

('50000000-0001-0000-0000-000000000021', 'es', 'Chin-ups con agarre supino', 'chin ups con agarre supino biceps',
'["Colgá de una barra con agarre supino al ancho de los hombros.", "Tirate hacia arriba llevando el mentón por encima de la barra.", "Enfocá la contracción en los bíceps además de los dorsales.", "Bajá de forma controlada hasta la extensión completa.", "Este ejercicio trabaja bíceps y espalda simultáneamente."]'::jsonb),

('50000000-0001-0000-0000-000000000022', 'es', 'Curl tipo W con mancuernas', 'curl tipo w con mancuernas wide grip',
'["Parate con una mancuerna en cada mano, brazos a los costados.", "Separá los codos ligeramente del cuerpo formando una W.", "Flexioná los codos llevando las mancuernas hacia los hombros.", "Mantené los codos en esa posición abierta durante todo el movimiento.", "Bajá de forma controlada."]'::jsonb),

('50000000-0001-0000-0000-000000000023', 'es', 'Curl con mancuerna unilateral en polea', 'curl con mancuerna unilateral en polea single arm cable',
'["Parate al lado de una polea baja con un agarre de mano conectado.", "Tomá el agarre con una mano, palma hacia arriba.", "Flexioná el codo para subir el agarre hacia el hombro.", "Mantené el codo fijo al costado del cuerpo.", "Bajá de forma controlada y repetí. Cambiá de brazo."]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('50000000-0001-0000-0000-000000000001', 'en', 'Barbell Curl', 'barbell curl straight bar',
'["Stand with feet shoulder-width apart, holding bar with underhand grip (palms up).", "Keep elbows close to body and core engaged.", "Bend elbows to lift bar toward shoulders without moving upper arms.", "Contract biceps at top of movement.", "Lower bar in controlled manner to full extension."]'::jsonb),

('50000000-0001-0000-0000-000000000002', 'en', 'EZ Bar Curl', 'ez bar curl',
'["Grip EZ bar on angled portions with underhand grip.", "Stand upright with elbows close to body.", "Bend elbows to raise bar toward shoulders.", "Keep upper arms fixed throughout movement.", "Lower in controlled manner until arms fully extended."]'::jsonb),

('50000000-0001-0000-0000-000000000003', 'en', 'Alternating Dumbbell Curl', 'alternating dumbbell curl',
'["Stand with dumbbell in each hand, arms at sides, palms facing body.", "Bend one elbow and rotate wrist during ascent until palm faces up.", "Raise dumbbell toward shoulder contracting bicep.", "Lower in controlled manner while repeating with other arm.", "Avoid swinging body or using momentum."]'::jsonb),

('50000000-0001-0000-0000-000000000004', 'en', 'Hammer Curl', 'hammer curl neutral grip',
'["Stand with dumbbell in each hand, palms facing each other (neutral grip).", "Keep elbows close to body.", "Bend elbows to raise dumbbells toward shoulders without rotating wrists.", "Contract at top and lower in controlled manner.", "This grip works brachialis and brachioradialis more."]'::jsonb),

('50000000-0001-0000-0000-000000000005', 'en', 'Concentration Curl', 'concentration curl seated',
'["Sit on bench with legs apart, dumbbell in one hand.", "Rest elbow against inner thigh.", "Bend elbow to raise dumbbell toward shoulder.", "Strongly contract bicep at top.", "Lower in controlled manner and repeat. Switch arms when done."]'::jsonb),

('50000000-0001-0000-0000-000000000006', 'en', 'Incline Dumbbell Curl', 'incline dumbbell curl bench',
'["Lie on incline bench (45 degrees) with dumbbell in each hand, arms hanging.", "Keep elbows fixed while bending to raise dumbbells.", "Contract biceps at top of movement.", "Lower in controlled manner to full extension.", "Incline increases bicep stretch."]'::jsonb),

('50000000-0001-0000-0000-000000000007', 'en', 'Cable Curl', 'cable curl low pulley',
'["Stand facing low pulley with bar or handle attached.", "Grip handle with palms up.", "Bend elbows to raise handle toward shoulders.", "Keep elbows fixed at sides.", "Lower in controlled manner maintaining constant tension."]'::jsonb),

('50000000-0001-0000-0000-000000000008', 'en', 'Preacher Curl with Barbell', 'preacher curl barbell',
'["Sit at preacher bench with arms resting on pad.", "Grip bar with underhand grip.", "Bend elbows to raise bar toward shoulders.", "Contract biceps without lifting arms off pad.", "Lower in controlled manner to near full extension."]'::jsonb),

('50000000-0001-0000-0000-000000000009', 'en', 'Preacher Curl with Dumbbell', 'preacher curl dumbbell single arm',
'["Sit at preacher bench with one arm resting on pad.", "Hold dumbbell with underhand grip.", "Bend elbow to raise dumbbell toward shoulder.", "Lower in controlled manner without fully extending elbow.", "Complete reps and switch arms."]'::jsonb),

('50000000-0001-0000-0000-000000000010', 'en', 'Spider Curl', 'spider curl prone incline',
'["Lie face down on incline bench with arms hanging.", "Hold dumbbells or bar with underhand grip.", "Bend elbows to raise weight toward shoulders.", "Strongly contract biceps at top.", "Lower in controlled manner. Gravity increases tension."]'::jsonb),

('50000000-0001-0000-0000-000000000011', 'en', '21s Curl', '21s curl twentyone',
'["Perform 7 partial reps from bottom to middle of range.", "Then 7 partial reps from middle to top.", "Finally 7 full reps with complete range.", "Keep elbows fixed throughout set.", "This method increases time under tension."]'::jsonb),

('50000000-0001-0000-0000-000000000012', 'en', 'Machine Bicep Curl', 'machine bicep curl',
'["Adjust seat so elbows align with machine axis.", "Grip handles with underhand grip.", "Bend elbows to raise handles toward shoulders.", "Contract biceps and lower in controlled manner.", "Machine provides constant tension throughout movement."]'::jsonb),

('50000000-0001-0000-0000-000000000013', 'en', 'Reverse Curl', 'reverse curl overhand grip pronated',
'["Grip bar with overhand grip (palms down).", "Stand upright with elbows close to body.", "Bend elbows to raise bar toward shoulders.", "Lower in controlled manner.", "This grip emphasizes brachioradialis and forearm extensors."]'::jsonb),

('50000000-0001-0000-0000-000000000014', 'en', 'Wrist Curl', 'wrist curl forearm flexors',
'["Sit with forearms resting on thighs, wrists past knees.", "Hold bar or dumbbells with underhand grip.", "Flex wrists to raise weight.", "Lower in controlled manner extending wrists.", "This exercise works forearm flexors."]'::jsonb),

('50000000-0001-0000-0000-000000000015', 'en', 'Reverse Wrist Curl', 'reverse wrist curl forearm extensors',
'["Sit with forearms resting on thighs, wrists past knees.", "Hold bar or dumbbells with overhand grip.", "Extend wrists to raise weight.", "Lower in controlled manner flexing wrists.", "This exercise works forearm extensors."]'::jsonb),

('50000000-0001-0000-0000-000000000016', 'en', 'Cross-Body Hammer Curl', 'cross body hammer curl',
'["Stand with dumbbell in each hand at sides.", "Bend one elbow and bring dumbbell toward opposite shoulder crossing body.", "Keep elbow close to torso during movement.", "Lower in controlled manner and repeat with other arm.", "This exercise emphasizes brachialis."]'::jsonb),

('50000000-0001-0000-0000-000000000017', 'en', 'Overhead Cable Curl', 'overhead cable curl high pulley',
'["Stand between two high pulleys with cables at shoulder height.", "Grip handles with underhand grip, arms extended to sides.", "Bend elbows bringing hands toward head.", "Contract biceps and return in controlled manner to starting position.", "Keep upper arms parallel to floor."]'::jsonb),

('50000000-0001-0000-0000-000000000018', 'en', 'Resistance Band Curl', 'resistance band curl',
'["Step on band with both feet and hold ends with hands.", "Keep elbows close to body.", "Bend elbows to raise hands toward shoulders.", "Contract biceps and lower in controlled manner.", "Resistance increases as you stretch band."]'::jsonb),

('50000000-0001-0000-0000-000000000019', 'en', 'Zottman Curl', 'zottman curl dumbbell rotation',
'["Stand with dumbbell in each hand, palms up.", "Bend elbows to raise dumbbells toward shoulders.", "At top, rotate wrists until palms face down.", "Lower dumbbells with this overhand grip.", "Rotate wrists again at bottom for next rep."]'::jsonb),

('50000000-0001-0000-0000-000000000020', 'en', 'Drag Curl', 'drag curl barbell',
'["Stand holding bar with underhand grip in front of thighs.", "Instead of curling forward, bring elbows back while raising bar.", "Bar should drag close to body.", "Raise until bar reaches chest.", "Lower in controlled manner dragging bar."]'::jsonb),

('50000000-0001-0000-0000-000000000021', 'en', 'Supinated Grip Chin-Ups', 'supinated grip chin ups bicep pullup',
'["Hang from bar with underhand grip at shoulder width.", "Pull yourself up bringing chin over bar.", "Focus contraction on biceps along with lats.", "Lower in controlled manner to full extension.", "This exercise works biceps and back simultaneously."]'::jsonb),

('50000000-0001-0000-0000-000000000022', 'en', 'W Curl with Dumbbells', 'w curl dumbbells wide grip',
'["Stand with dumbbell in each hand, arms at sides.", "Separate elbows slightly from body forming a W.", "Bend elbows bringing dumbbells toward shoulders.", "Keep elbows in that open position throughout movement.", "Lower in controlled manner."]'::jsonb),

('50000000-0001-0000-0000-000000000023', 'en', 'Single Arm Cable Curl', 'single arm cable curl unilateral',
'["Stand beside low pulley with handle attached.", "Grip handle with one hand, palm up.", "Bend elbow to raise handle toward shoulder.", "Keep elbow fixed at side of body.", "Lower in controlled manner and repeat. Switch arms."]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '50000000-0001-%'
GROUP BY language_code;
