-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: CORE
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('60000000-0001-0000-0000-000000000001', 'es', 'Plancha abdominal', 'plancha abdominal plank core',
'["Colocate boca abajo apoyando los antebrazos y las puntas de los pies en el suelo.", "Mantené el cuerpo en línea recta desde la cabeza hasta los talones.", "Activá el core contrayendo el abdomen y los glúteos.", "Mantené la posición sin dejar que las caderas suban o bajen.", "Respirá de forma controlada durante todo el ejercicio."]'::jsonb),

('60000000-0001-0000-0000-000000000002', 'es', 'Plancha lateral', 'plancha lateral side plank obliques',
'["Acostate de lado apoyándote sobre un antebrazo con el codo debajo del hombro.", "Elevá las caderas para formar una línea recta desde la cabeza hasta los pies.", "Mantené el core activado y evitá que las caderas caigan.", "Podés apilar los pies o escalonarlos para mayor estabilidad.", "Mantené la posición y luego cambiá de lado."]'::jsonb),

('60000000-0001-0000-0000-000000000003', 'es', 'Crunch abdominal', 'crunch abdominal',
'["Acostate boca arriba con las rodillas flexionadas y los pies apoyados en el suelo.", "Colocá las manos detrás de la cabeza o cruzadas sobre el pecho.", "Contraé el abdomen para elevar los hombros del suelo.", "Mantené la contracción un segundo y bajá de forma controlada.", "Evitá tirar del cuello con las manos."]'::jsonb),

('60000000-0001-0000-0000-000000000004', 'es', 'Crunch en máquina', 'crunch en maquina machine ab',
'["Ajustá el asiento y la resistencia de la máquina según tu nivel.", "Sentate con los pies fijos y tomá las empuñaduras o apoyá el pecho en la almohadilla.", "Contraé el abdomen para flexionar el torso hacia adelante.", "Mantené la contracción un segundo y volvé de forma controlada.", "Evitá usar los brazos para tirar del peso."]'::jsonb),

('60000000-0001-0000-0000-000000000005', 'es', 'Elevación de piernas colgado', 'elevacion de piernas colgado hanging leg raise',
'["Colgá de una barra con los brazos extendidos y el cuerpo recto.", "Mantené las piernas juntas y elevá las piernas flexionando las caderas.", "Subí hasta que las piernas queden paralelas al suelo o más arriba.", "Bajá de forma controlada sin balancearte.", "Para mayor dificultad, mantené las piernas rectas."]'::jsonb),

('60000000-0001-0000-0000-000000000006', 'es', 'Elevación de piernas en paralelas', 'elevacion de piernas en paralelas captain chair',
'["Apoyá los antebrazos en las almohadillas de la estación de paralelas.", "Dejá las piernas colgando con el cuerpo recto.", "Elevá las piernas flexionando las caderas hasta que queden paralelas al suelo.", "Bajá de forma controlada sin balancearte.", "Mantené la espalda apoyada contra el respaldo."]'::jsonb),

('60000000-0001-0000-0000-000000000007', 'es', 'Mountain climbers', 'mountain climbers escaladores',
'["Colocate en posición de plancha alta con las manos debajo de los hombros.", "Llevá una rodilla hacia el pecho manteniendo el core activado.", "Volvé la pierna a la posición inicial mientras llevás la otra rodilla al pecho.", "Alterná las piernas de forma rápida y controlada.", "Mantené las caderas bajas y el cuerpo en línea recta."]'::jsonb),

('60000000-0001-0000-0000-000000000008', 'es', 'Russian twist', 'russian twist oblicuos rotacion',
'["Sentate en el suelo con las rodillas flexionadas y los pies ligeramente elevados.", "Incliná el torso hacia atrás formando un ángulo de 45 grados.", "Sostené un peso con ambas manos frente al pecho.", "Rotá el torso llevando el peso hacia un lado y luego hacia el otro.", "Mantené el core activado durante todo el movimiento."]'::jsonb),

('60000000-0001-0000-0000-000000000009', 'es', 'Rueda abdominal (Ab wheel)', 'rueda abdominal ab wheel rollout',
'["Arrodillate en el suelo sosteniendo la rueda abdominal con ambas manos.", "Mantené el core firme y la espalda recta.", "Rodá la rueda hacia adelante extendiendo el cuerpo lo más posible.", "Contraé el abdomen para volver a la posición inicial.", "Evitá arquear la espalda baja durante el movimiento."]'::jsonb),

('60000000-0001-0000-0000-000000000010', 'es', 'Dead bug', 'dead bug bicho muerto core',
'["Acostate boca arriba con los brazos extendidos hacia el techo y las rodillas flexionadas a 90 grados.", "Mantené la espalda baja pegada al suelo.", "Extendé un brazo hacia atrás y la pierna opuesta hacia adelante simultáneamente.", "Volvé a la posición inicial y repetí con el otro lado.", "Movete de forma lenta y controlada."]'::jsonb),

('60000000-0001-0000-0000-000000000011', 'es', 'Bird dog', 'bird dog cuadrupedia core',
'["Colocate en posición de cuadrupedia con las manos bajo los hombros y rodillas bajo las caderas.", "Extendé un brazo hacia adelante y la pierna opuesta hacia atrás simultáneamente.", "Mantené la espalda recta y el core activado.", "Volvé a la posición inicial de forma controlada.", "Alterná lados con cada repetición."]'::jsonb),

('60000000-0001-0000-0000-000000000012', 'es', 'Crunch en polea alta', 'crunch en polea alta cable kneeling',
'["Arrodillate frente a una polea alta con una cuerda conectada.", "Sostené la cuerda detrás de la cabeza o a los lados de la cara.", "Contraé el abdomen para flexionar el torso hacia el suelo.", "Mantené la contracción un segundo y volvé de forma controlada.", "Evitá tirar con los brazos; el movimiento viene del core."]'::jsonb),

('60000000-0001-0000-0000-000000000013', 'es', 'Hollow body hold', 'hollow body hold gimnasia',
'["Acostate boca arriba con los brazos extendidos por encima de la cabeza.", "Elevá los hombros y las piernas del suelo manteniendo la zona lumbar pegada.", "Formá una curva en forma de banana con el cuerpo.", "Mantené la posición contrayendo fuertemente el abdomen.", "Respirá de forma controlada sin perder la forma."]'::jsonb),

('60000000-0001-0000-0000-000000000014', 'es', 'Crunch bicicleta', 'crunch bicicleta bicycle abs',
'["Acostate boca arriba con las manos detrás de la cabeza y las piernas elevadas.", "Llevá un codo hacia la rodilla opuesta mientras extendés la otra pierna.", "Alterná de forma continua como si pedaleases una bicicleta.", "Mantené el movimiento controlado y el abdomen contraído.", "Evitá tirar del cuello con las manos."]'::jsonb),

('60000000-0001-0000-0000-000000000015', 'es', 'V-ups', 'v ups elevacion simultanea',
'["Acostate boca arriba con los brazos extendidos por encima de la cabeza.", "Elevá simultáneamente el torso y las piernas rectas formando una V.", "Intentá tocar los pies con las manos en la parte alta.", "Bajá de forma controlada a la posición inicial.", "Mantené el core activado durante todo el movimiento."]'::jsonb),

('60000000-0001-0000-0000-000000000016', 'es', 'Crunch inverso', 'crunch inverso reverse',
'["Acostate boca arriba con los brazos a los costados y las piernas elevadas.", "Flexioná las rodillas a 90 grados.", "Contraé el abdomen para elevar las caderas del suelo llevando las rodillas hacia el pecho.", "Bajá de forma controlada sin usar impulso.", "Evitá balancearte o empujarte con los brazos."]'::jsonb),

('60000000-0001-0000-0000-000000000017', 'es', 'Leñador en polea (Woodchop)', 'lenador en polea woodchop cable rotacion',
'["Parate de lado a una polea alta con ambas manos en el agarre.", "Tirá del cable hacia abajo y cruzá el cuerpo hacia la cadera opuesta.", "Rotá el torso y las caderas durante el movimiento.", "Volvé de forma controlada a la posición inicial.", "Mantené los brazos relativamente rectos; la fuerza viene del core."]'::jsonb),

('60000000-0001-0000-0000-000000000018', 'es', 'Pallof press', 'pallof press antirotacion core',
'["Parate de lado a una polea a la altura del pecho.", "Sostené el agarre con ambas manos frente al esternón.", "Extendé los brazos hacia adelante resistiendo la rotación del cable.", "Mantené la posición un momento y volvé a la posición inicial.", "Repetí y luego cambiá de lado."]'::jsonb),

('60000000-0001-0000-0000-000000000019', 'es', 'Plancha con toque de hombro', 'plancha con toque de hombro shoulder tap',
'["Colocate en posición de plancha alta con las manos debajo de los hombros.", "Mantené las caderas estables y el core activado.", "Levantá una mano y tocá el hombro opuesto.", "Volvé la mano al suelo y repetí con el otro lado.", "Evitá que las caderas roten durante el movimiento."]'::jsonb),

('60000000-0001-0000-0000-000000000020', 'es', 'Sit-ups', 'sit ups abdominales completos',
'["Acostate boca arriba con las rodillas flexionadas y los pies fijos en el suelo.", "Colocá las manos detrás de la cabeza o cruzadas sobre el pecho.", "Contraé el abdomen para elevar todo el torso hasta sentarte.", "Bajá de forma controlada a la posición inicial.", "Evitá usar impulso o tirar del cuello."]'::jsonb),

('60000000-0001-0000-0000-000000000021', 'es', 'Plancha con elevación de pierna', 'plancha con elevacion de pierna leg lift plank',
'["Colocate en posición de plancha sobre antebrazos.", "Mantené el cuerpo en línea recta y el core activado.", "Elevá una pierna del suelo unos centímetros.", "Mantené la posición un momento y bajá la pierna.", "Alterná con la otra pierna."]'::jsonb),

('60000000-0001-0000-0000-000000000022', 'es', 'Rodillas al pecho tumbado', 'rodillas al pecho tumbado knee tuck lying',
'["Acostate boca arriba con las piernas extendidas y los brazos a los costados.", "Contraé el abdomen y llevá ambas rodillas hacia el pecho.", "Mantené la contracción un segundo.", "Extendé las piernas de forma controlada sin tocar el suelo.", "Repetí el movimiento sin descansar."]'::jsonb),

('60000000-0001-0000-0000-000000000023', 'es', 'Crunch oblicuo', 'crunch oblicuo lateral',
'["Acostate boca arriba con las rodillas flexionadas y los pies en el suelo.", "Colocá una mano detrás de la cabeza y la otra en el abdomen.", "Elevá el torso rotando para llevar el codo hacia la rodilla opuesta.", "Bajá de forma controlada y repetí.", "Completá las repeticiones de un lado antes de cambiar."]'::jsonb),

('60000000-0001-0000-0000-000000000024', 'es', 'Flexión lateral de tronco', 'flexion lateral de tronco side bend obliques',
'["Parate con los pies al ancho de los hombros sosteniendo una mancuerna en una mano.", "Mantené el brazo pegado al costado del cuerpo.", "Inclinate hacia el lado de la mancuerna flexionando lateralmente.", "Volvé a la posición vertical contrayendo el oblicuo opuesto.", "Completá las repeticiones y cambiá de lado."]'::jsonb),

('60000000-0001-0000-0000-000000000025', 'es', 'Peso muerto con una pierna', 'peso muerto con una pierna single leg deadlift',
'["Parate sobre una pierna sosteniendo una mancuerna o kettlebell.", "Flexioná la cadera llevando el torso hacia adelante y la pierna libre hacia atrás.", "Mantené la espalda recta y bajá hasta que el torso esté casi paralelo al suelo.", "Volvé a la posición inicial contrayendo glúteos y core.", "Completá las repeticiones y cambiá de pierna."]'::jsonb),

('60000000-0001-0000-0000-000000000026', 'es', 'Farmer carry', 'farmer carry caminata con peso',
'["Parate sosteniendo una mancuerna o kettlebell pesada en cada mano.", "Mantené el core activado y los hombros hacia atrás.", "Caminá de forma controlada una distancia determinada.", "Mantené una postura erguida sin inclinarte hacia los lados.", "Este ejercicio trabaja core, agarre y estabilidad general."]'::jsonb),

('60000000-0001-0000-0000-000000000027', 'es', 'Suitcase carry', 'suitcase carry caminata unilateral',
'["Parate sosteniendo una mancuerna o kettlebell pesada en una sola mano.", "Mantené el core activado para evitar inclinarte hacia el peso.", "Caminá de forma controlada una distancia determinada.", "Cambiá de mano y repetí.", "Este ejercicio trabaja especialmente los oblicuos."]'::jsonb),

('60000000-0001-0000-0000-000000000028', 'es', 'Giro de torso sentado', 'giro de torso sentado seated twist machine',
'["Sentate en la máquina de rotación con los pies fijos.", "Tomá las empuñaduras o apoyá el pecho contra la almohadilla.", "Rotá el torso hacia un lado contra la resistencia.", "Volvé al centro de forma controlada y rotá hacia el otro lado.", "Mantené las caderas fijas durante todo el movimiento."]'::jsonb),

('60000000-0001-0000-0000-000000000029', 'es', 'L-sit', 'l sit paralelas gimnasia core',
'["Colocate entre barras paralelas o sobre el suelo con las manos a los costados.", "Extendé las piernas hacia adelante formando una L con el cuerpo.", "Mantené el core fuertemente contraído y los hombros bajos.", "Sostenete con los brazos rectos.", "Mantené la posición el mayor tiempo posible."]'::jsonb),

('60000000-0001-0000-0000-000000000030', 'es', 'Crunch con cable de pie', 'crunch con cable de pie standing cable crunch',
'["Parate de espaldas a una polea alta con la cuerda detrás de la cabeza.", "Mantené los pies al ancho de los hombros y las rodillas ligeramente flexionadas.", "Contraé el abdomen flexionando el torso hacia adelante.", "Mantené los brazos fijos; el movimiento viene del core.", "Volvé de forma controlada a la posición inicial."]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('60000000-0001-0000-0000-000000000001', 'en', 'Plank', 'plank core abdominal',
'["Get face down supporting yourself on forearms and toes.", "Keep body in straight line from head to heels.", "Engage core by contracting abs and glutes.", "Hold position without letting hips rise or drop.", "Breathe in controlled manner throughout exercise."]'::jsonb),

('60000000-0001-0000-0000-000000000002', 'en', 'Side Plank', 'side plank obliques lateral',
'["Lie on side supporting yourself on one forearm with elbow under shoulder.", "Raise hips to form straight line from head to feet.", "Keep core engaged and avoid letting hips drop.", "You can stack feet or stagger them for more stability.", "Hold position then switch sides."]'::jsonb),

('60000000-0001-0000-0000-000000000003', 'en', 'Crunch', 'crunch abdominal basic',
'["Lie on back with knees bent and feet flat on floor.", "Place hands behind head or crossed over chest.", "Contract abs to lift shoulders off floor.", "Hold contraction for a second and lower in controlled manner.", "Avoid pulling on neck with hands."]'::jsonb),

('60000000-0001-0000-0000-000000000004', 'en', 'Machine Crunch', 'machine crunch ab',
'["Adjust seat and resistance according to your level.", "Sit with feet fixed and grip handles or rest chest on pad.", "Contract abs to flex torso forward.", "Hold contraction for a second and return in controlled manner.", "Avoid using arms to pull weight."]'::jsonb),

('60000000-0001-0000-0000-000000000005', 'en', 'Hanging Leg Raise', 'hanging leg raise bar',
'["Hang from bar with arms extended and body straight.", "Keep legs together and raise legs by flexing hips.", "Raise until legs are parallel to floor or higher.", "Lower in controlled manner without swinging.", "For more difficulty, keep legs straight."]'::jsonb),

('60000000-0001-0000-0000-000000000006', 'en', 'Captain''s Chair Leg Raise', 'captain chair leg raise parallel bars',
'["Rest forearms on pads of captain''s chair station.", "Let legs hang with body straight.", "Raise legs by flexing hips until parallel to floor.", "Lower in controlled manner without swinging.", "Keep back against backrest."]'::jsonb),

('60000000-0001-0000-0000-000000000007', 'en', 'Mountain Climbers', 'mountain climbers cardio core',
'["Get in high plank position with hands under shoulders.", "Bring one knee toward chest keeping core engaged.", "Return leg to starting position while bringing other knee to chest.", "Alternate legs quickly and controlled.", "Keep hips low and body in straight line."]'::jsonb),

('60000000-0001-0000-0000-000000000008', 'en', 'Russian Twist', 'russian twist obliques rotation',
'["Sit on floor with knees bent and feet slightly elevated.", "Lean torso back forming 45-degree angle.", "Hold weight with both hands in front of chest.", "Rotate torso bringing weight to one side then the other.", "Keep core engaged throughout movement."]'::jsonb),

('60000000-0001-0000-0000-000000000009', 'en', 'Ab Wheel Rollout', 'ab wheel rollout core',
'["Kneel on floor holding ab wheel with both hands.", "Keep core tight and back straight.", "Roll wheel forward extending body as far as possible.", "Contract abs to return to starting position.", "Avoid arching lower back during movement."]'::jsonb),

('60000000-0001-0000-0000-000000000010', 'en', 'Dead Bug', 'dead bug core stability',
'["Lie on back with arms extended toward ceiling and knees bent at 90 degrees.", "Keep lower back pressed into floor.", "Extend one arm back and opposite leg forward simultaneously.", "Return to starting position and repeat on other side.", "Move slowly and controlled."]'::jsonb),

('60000000-0001-0000-0000-000000000011', 'en', 'Bird Dog', 'bird dog quadruped core',
'["Get on all fours with hands under shoulders and knees under hips.", "Extend one arm forward and opposite leg back simultaneously.", "Keep back straight and core engaged.", "Return to starting position in controlled manner.", "Alternate sides with each rep."]'::jsonb),

('60000000-0001-0000-0000-000000000012', 'en', 'Cable Crunch', 'cable crunch kneeling high pulley',
'["Kneel facing high pulley with rope attached.", "Hold rope behind head or at sides of face.", "Contract abs to flex torso toward floor.", "Hold contraction for a second and return in controlled manner.", "Avoid pulling with arms; movement comes from core."]'::jsonb),

('60000000-0001-0000-0000-000000000013', 'en', 'Hollow Body Hold', 'hollow body hold gymnastics',
'["Lie on back with arms extended overhead.", "Raise shoulders and legs off floor keeping lower back pressed down.", "Form banana-like curve with body.", "Hold position strongly contracting abs.", "Breathe controlled without losing form."]'::jsonb),

('60000000-0001-0000-0000-000000000014', 'en', 'Bicycle Crunch', 'bicycle crunch abs',
'["Lie on back with hands behind head and legs elevated.", "Bring one elbow toward opposite knee while extending other leg.", "Alternate continuously as if pedaling a bicycle.", "Keep movement controlled and abs contracted.", "Avoid pulling on neck with hands."]'::jsonb),

('60000000-0001-0000-0000-000000000015', 'en', 'V-Ups', 'v ups simultaneous raise',
'["Lie on back with arms extended overhead.", "Simultaneously raise torso and straight legs forming a V.", "Try to touch feet with hands at top.", "Lower in controlled manner to starting position.", "Keep core engaged throughout movement."]'::jsonb),

('60000000-0001-0000-0000-000000000016', 'en', 'Reverse Crunch', 'reverse crunch lower abs',
'["Lie on back with arms at sides and legs elevated.", "Bend knees at 90 degrees.", "Contract abs to raise hips off floor bringing knees toward chest.", "Lower in controlled manner without using momentum.", "Avoid swinging or pushing with arms."]'::jsonb),

('60000000-0001-0000-0000-000000000017', 'en', 'Cable Woodchop', 'cable woodchop rotation core',
'["Stand sideways to high pulley with both hands on handle.", "Pull cable down and across body toward opposite hip.", "Rotate torso and hips during movement.", "Return in controlled manner to starting position.", "Keep arms relatively straight; power comes from core."]'::jsonb),

('60000000-0001-0000-0000-000000000018', 'en', 'Pallof Press', 'pallof press anti rotation core',
'["Stand sideways to chest-height pulley.", "Hold handle with both hands in front of sternum.", "Extend arms forward resisting cable rotation.", "Hold position briefly and return to starting position.", "Repeat then switch sides."]'::jsonb),

('60000000-0001-0000-0000-000000000019', 'en', 'Plank Shoulder Tap', 'plank shoulder tap stability',
'["Get in high plank position with hands under shoulders.", "Keep hips stable and core engaged.", "Lift one hand and tap opposite shoulder.", "Return hand to floor and repeat on other side.", "Avoid letting hips rotate during movement."]'::jsonb),

('60000000-0001-0000-0000-000000000020', 'en', 'Sit-Ups', 'sit ups full abdominal',
'["Lie on back with knees bent and feet anchored to floor.", "Place hands behind head or crossed over chest.", "Contract abs to raise entire torso to sitting position.", "Lower in controlled manner to starting position.", "Avoid using momentum or pulling on neck."]'::jsonb),

('60000000-0001-0000-0000-000000000021', 'en', 'Plank Leg Lift', 'plank leg lift raise',
'["Get in forearm plank position.", "Keep body in straight line and core engaged.", "Raise one leg a few inches off floor.", "Hold position briefly and lower leg.", "Alternate with other leg."]'::jsonb),

('60000000-0001-0000-0000-000000000022', 'en', 'Lying Knee Tuck', 'lying knee tuck pull in',
'["Lie on back with legs extended and arms at sides.", "Contract abs and bring both knees toward chest.", "Hold contraction for a second.", "Extend legs in controlled manner without touching floor.", "Repeat movement without resting."]'::jsonb),

('60000000-0001-0000-0000-000000000023', 'en', 'Oblique Crunch', 'oblique crunch lateral',
'["Lie on back with knees bent and feet on floor.", "Place one hand behind head and other on abs.", "Raise torso rotating to bring elbow toward opposite knee.", "Lower in controlled manner and repeat.", "Complete reps on one side before switching."]'::jsonb),

('60000000-0001-0000-0000-000000000024', 'en', 'Side Bend', 'side bend obliques lateral flexion',
'["Stand with feet shoulder-width apart holding dumbbell in one hand.", "Keep arm pressed against side of body.", "Lean toward dumbbell side with lateral flexion.", "Return to vertical position contracting opposite oblique.", "Complete reps and switch sides."]'::jsonb),

('60000000-0001-0000-0000-000000000025', 'en', 'Single Leg Deadlift', 'single leg deadlift balance core',
'["Stand on one leg holding dumbbell or kettlebell.", "Hinge at hip bringing torso forward and free leg back.", "Keep back straight and lower until torso is nearly parallel to floor.", "Return to starting position contracting glutes and core.", "Complete reps and switch legs."]'::jsonb),

('60000000-0001-0000-0000-000000000026', 'en', 'Farmer Carry', 'farmer carry walk weighted',
'["Stand holding heavy dumbbell or kettlebell in each hand.", "Keep core engaged and shoulders back.", "Walk in controlled manner for set distance.", "Maintain upright posture without leaning to sides.", "This exercise works core, grip and overall stability."]'::jsonb),

('60000000-0001-0000-0000-000000000027', 'en', 'Suitcase Carry', 'suitcase carry unilateral walk',
'["Stand holding heavy dumbbell or kettlebell in one hand only.", "Keep core engaged to avoid leaning toward weight.", "Walk in controlled manner for set distance.", "Switch hands and repeat.", "This exercise especially works obliques."]'::jsonb),

('60000000-0001-0000-0000-000000000028', 'en', 'Seated Torso Rotation', 'seated torso rotation machine twist',
'["Sit on rotation machine with feet fixed.", "Grip handles or rest chest against pad.", "Rotate torso to one side against resistance.", "Return to center in controlled manner and rotate to other side.", "Keep hips fixed throughout movement."]'::jsonb),

('60000000-0001-0000-0000-000000000029', 'en', 'L-Sit', 'l sit parallel bars gymnastics core',
'["Position yourself between parallel bars or on floor with hands at sides.", "Extend legs forward forming L shape with body.", "Keep core strongly contracted and shoulders down.", "Support yourself with straight arms.", "Hold position as long as possible."]'::jsonb),

('60000000-0001-0000-0000-000000000030', 'en', 'Standing Cable Crunch', 'standing cable crunch abs',
'["Stand with back to high pulley with rope behind head.", "Keep feet shoulder-width apart and knees slightly bent.", "Contract abs flexing torso forward.", "Keep arms fixed; movement comes from core.", "Return in controlled manner to starting position."]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '60000000-0001-%'
GROUP BY language_code;
