-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: CUERPO COMPLETO
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('80000000-0001-0000-0000-000000000001', 'es', 'Burpees', 'burpees cuerpo completo cardio',
'["Parate con los pies al ancho de los hombros.", "Bajá a una sentadilla y colocá las manos en el suelo.", "Saltá con los pies hacia atrás para quedar en posición de plancha.", "Realizá una flexión de brazos (opcional).", "Saltá con los pies hacia las manos y explotá hacia arriba con un salto.", "Repetí de forma fluida y controlada."]'::jsonb),

('80000000-0001-0000-0000-000000000002', 'es', 'Clean and Press con barra', 'clean and press con barra cargada',
'["Parate con la barra en el suelo frente a vos, pies al ancho de las caderas.", "Flexioná caderas y rodillas para tomar la barra con agarre pronado.", "Tirá la barra hacia arriba con un movimiento explosivo llevándola a los hombros.", "Desde los hombros, empujá la barra hacia arriba hasta extender los brazos.", "Bajá la barra a los hombros y luego al suelo de forma controlada."]'::jsonb),

('80000000-0001-0000-0000-000000000003', 'es', 'Thrusters', 'thrusters sentadilla press',
'["Sostené una barra en posición de front squat o mancuernas a la altura de los hombros.", "Realizá una sentadilla profunda manteniendo el pecho arriba.", "Al subir, usá el impulso para empujar el peso por encima de la cabeza.", "Bajá el peso a los hombros mientras descendés a la siguiente sentadilla.", "El movimiento debe ser fluido y continuo."]'::jsonb),

('80000000-0001-0000-0000-000000000004', 'es', 'Turkish get-up', 'turkish get up levantada turca kettlebell',
'["Acostate boca arriba sosteniendo una kettlebell con un brazo extendido hacia el techo.", "Flexioná la rodilla del mismo lado y apoyá el pie en el suelo.", "Elevá el torso apoyándote en el codo opuesto, luego en la mano.", "Levantá las caderas del suelo y pasá la pierna libre por debajo para arrodillarte.", "Ponete de pie manteniendo el peso arriba. Revertí el movimiento para bajar."]'::jsonb),

('80000000-0001-0000-0000-000000000005', 'es', 'Swing con kettlebell', 'swing con kettlebell balanceo',
'["Parate con los pies más anchos que los hombros, kettlebell en el suelo frente a vos.", "Flexioná las caderas para tomar la kettlebell con ambas manos.", "Balanceá la kettlebell entre las piernas y luego impulsala hacia adelante con las caderas.", "Llevá la kettlebell hasta la altura de los hombros con los brazos extendidos.", "Dejá que la kettlebell baje y repetí el balanceo de forma fluida."]'::jsonb),

('80000000-0001-0000-0000-000000000006', 'es', 'Clean con barra', 'clean con barra cargada',
'["Colocá la barra en el suelo frente a vos con los pies al ancho de las caderas.", "Flexioná caderas y rodillas para tomar la barra con agarre pronado.", "Tirá la barra hacia arriba explosivamente usando caderas y piernas.", "Metete debajo de la barra girando los codos para recibirla en los hombros.", "Ponete de pie completamente y luego bajá la barra de forma controlada."]'::jsonb),

('80000000-0001-0000-0000-000000000007', 'es', 'Snatch con barra', 'snatch con barra arranque',
'["Colocá la barra en el suelo y tomala con agarre ancho.", "Flexioná caderas y rodillas manteniendo la espalda recta.", "Tirá la barra hacia arriba explosivamente, llevándola directamente sobre la cabeza.", "Metete debajo de la barra en posición de sentadilla con los brazos bloqueados.", "Ponete de pie completando el levantamiento."]'::jsonb),

('80000000-0001-0000-0000-000000000008', 'es', 'Man makers', 'man makers mancuernas remo flexion',
'["Comenzá en posición de plancha con una mancuerna en cada mano.", "Realizá una flexión de brazos.", "Hacé un remo con un brazo, luego con el otro.", "Saltá con los pies hacia las manos.", "Ponete de pie y realizá un clean and press con las mancuernas.", "Bajá las mancuernas y volvé a la plancha."]'::jsonb),

('80000000-0001-0000-0000-000000000009', 'es', 'Box jumps', 'box jumps salto al cajon pliometrico',
'["Parate frente a una caja o banco resistente.", "Flexioná las rodillas y balanceá los brazos hacia atrás.", "Saltá explosivamente hacia arriba y adelante aterrizando sobre la caja.", "Aterrizá suavemente con ambos pies y las rodillas ligeramente flexionadas.", "Bajá de la caja (paso o salto) y repetí."]'::jsonb),

('80000000-0001-0000-0000-000000000010', 'es', 'Renegade rows', 'renegade rows remo plancha',
'["Colocate en posición de plancha con una mancuerna en cada mano.", "Mantené el core firme y las caderas estables.", "Hacé un remo con un brazo llevando la mancuerna hacia la cadera.", "Bajá la mancuerna y repetí con el otro brazo.", "Evitá rotar las caderas durante el movimiento."]'::jsonb),

('80000000-0001-0000-0000-000000000011', 'es', 'Sentadilla con salto', 'sentadilla con salto jump squat',
'["Parate con los pies al ancho de los hombros.", "Bajá a una sentadilla profunda con los brazos hacia atrás.", "Explotá hacia arriba saltando lo más alto posible.", "Aterrizá suavemente volviendo directamente a la sentadilla.", "Repetí de forma continua manteniendo buena forma."]'::jsonb),

('80000000-0001-0000-0000-000000000012', 'es', 'Wall balls', 'wall balls pelota pared funcional',
'["Parate frente a una pared sosteniendo un balón medicinal contra el pecho.", "Realizá una sentadilla profunda.", "Al subir, empujá el balón hacia arriba golpeando la pared a una altura marcada.", "Atrapá el balón en el descenso y bajá directamente a la siguiente sentadilla.", "Mantené un ritmo constante."]'::jsonb),

('80000000-0001-0000-0000-000000000013', 'es', 'Escaladores de montaña (Mountain climbers)', 'escaladores de montana mountain climbers',
'["Colocate en posición de plancha alta con las manos bajo los hombros.", "Llevá una rodilla hacia el pecho manteniendo el core activado.", "Volvé la pierna a la posición inicial mientras llevás la otra rodilla al pecho.", "Alterná las piernas rápidamente como si corrieras en el lugar.", "Mantené las caderas bajas y el cuerpo en línea recta."]'::jsonb),

('80000000-0001-0000-0000-000000000014', 'es', 'Clean con mancuernas', 'clean con mancuernas cargada',
'["Parate con una mancuerna en cada mano frente a los muslos.", "Flexioná ligeramente las rodillas y caderas.", "Tirá las mancuernas hacia arriba explosivamente usando las caderas.", "Girá las muñecas para recibir las mancuernas en los hombros.", "Bajá las mancuernas de forma controlada y repetí."]'::jsonb),

('80000000-0001-0000-0000-000000000015', 'es', 'Devil press', 'devil press mancuernas burpee',
'["Comenzá de pie con las mancuernas en el suelo.", "Bajá a una flexión con las manos sobre las mancuernas.", "Realizá la flexión y saltá con los pies hacia las mancuernas.", "Hacé un swing con las mancuernas entre las piernas y llevalas sobre la cabeza.", "Bajá las mancuernas al suelo y repetí."]'::jsonb),

('80000000-0001-0000-0000-000000000016', 'es', 'Arratre de trineo', 'arrastre de trineo sled push',
'["Colocate detrás del trineo con las manos en las empuñaduras.", "Incliná el torso hacia adelante y activá el core.", "Empujá el trineo caminando o corriendo hacia adelante.", "Mantené los brazos extendidos y empujá con las piernas.", "Continuá por la distancia o tiempo indicado."]'::jsonb),

('80000000-0001-0000-0000-000000000017', 'es', 'Battle ropes', 'battle ropes cuerdas de batalla ondas',
'["Parate con los pies al ancho de los hombros sosteniendo un extremo de cada cuerda.", "Flexioná ligeramente las rodillas y activá el core.", "Movete los brazos arriba y abajo alternadamente creando ondas en las cuerdas.", "Mantené un ritmo rápido y constante.", "Podés variar con ondas simultáneas, círculos o golpes."]'::jsonb),

('80000000-0001-0000-0000-000000000018', 'es', 'Estocada con press', 'estocada con press lunge press mancuernas',
'["Parate sosteniendo mancuernas a la altura de los hombros.", "Dá un paso adelante y bajá a una estocada.", "Mientras subís, empujá las mancuernas sobre la cabeza.", "Bajá las mancuernas mientras volvés a la posición inicial.", "Alterná las piernas con cada repetición."]'::jsonb),

('80000000-0001-0000-0000-000000000019', 'es', 'Sentadilla con curl y press', 'sentadilla con curl y press squat curl press',
'["Parate sosteniendo mancuernas a los costados.", "Realizá una sentadilla profunda.", "Al subir, hacé un curl con las mancuernas.", "Continuá el movimiento empujando las mancuernas sobre la cabeza.", "Bajá las mancuernas y repetí."]'::jsonb),

('80000000-0001-0000-0000-000000000020', 'es', 'Hang clean', 'hang clean cargada colgante',
'["Parate sosteniendo la barra a la altura de las caderas con agarre pronado.", "Flexioná ligeramente las rodillas y caderas llevando la barra justo arriba de las rodillas.", "Tirá explosivamente usando las caderas y metete debajo de la barra.", "Recibí la barra en los hombros en posición de front squat.", "Ponete de pie y bajá la barra a la posición inicial."]'::jsonb),

('80000000-0001-0000-0000-000000000021', 'es', 'Snatch con mancuerna', 'snatch con mancuerna arranque',
'["Parate con los pies al ancho de los hombros, una mancuerna entre los pies.", "Flexioná caderas y rodillas para tomar la mancuerna con una mano.", "Tirá la mancuerna hacia arriba explosivamente extendiendo caderas y rodillas.", "Recibí la mancuerna sobre la cabeza con el brazo extendido.", "Bajá de forma controlada y repetí. Cambiá de brazo."]'::jsonb),

('80000000-0001-0000-0000-000000000022', 'es', 'Farmer walk', 'farmer walk caminata del granjero',
'["Parate sosteniendo una mancuerna o kettlebell pesada en cada mano.", "Mantené el core activado y los hombros hacia atrás.", "Caminá de forma controlada una distancia determinada.", "Mantené una postura erguida sin inclinarte.", "Este ejercicio trabaja fuerza de agarre, core y estabilidad."]'::jsonb),

('80000000-0001-0000-0000-000000000023', 'es', 'Jerk con barra', 'jerk con barra empujon',
'["Comenzá con la barra en los hombros en posición de front rack.", "Flexioná ligeramente las rodillas para generar impulso.", "Extendé explosivamente las piernas mientras empujás la barra hacia arriba.", "Dividí las piernas o flexionalas para meterte debajo de la barra.", "Recuperá la posición de pie con los brazos bloqueados arriba."]'::jsonb),

('80000000-0001-0000-0000-000000000024', 'es', 'Cuerda para trepar', 'cuerda para trepar rope climb',
'["Parate debajo de la cuerda con las manos lo más arriba posible.", "Sujetá la cuerda con los pies usando la técnica de envolver o pisar.", "Tirá con los brazos mientras empujás con las piernas.", "Repetí el proceso hasta llegar arriba.", "Bajá de forma controlada usando brazos y piernas."]'::jsonb),

('80000000-0001-0000-0000-000000000025', 'es', 'Bear crawl', 'bear crawl gateo del oso',
'["Colocate en posición de cuadrupedia con las rodillas ligeramente elevadas del suelo.", "Avanzá moviendo la mano derecha y el pie izquierdo al mismo tiempo.", "Luego mové la mano izquierda y el pie derecho.", "Mantené la espalda plana y las caderas bajas.", "Avanzá la distancia indicada o por tiempo."]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('80000000-0001-0000-0000-000000000001', 'en', 'Burpees', 'burpees full body cardio',
'["Stand with feet shoulder-width apart.", "Lower into squat and place hands on floor.", "Jump feet back to plank position.", "Perform push-up (optional).", "Jump feet toward hands and explode up with a jump.", "Repeat in fluid and controlled manner."]'::jsonb),

('80000000-0001-0000-0000-000000000002', 'en', 'Barbell Clean and Press', 'barbell clean and press',
'["Stand with bar on floor in front of you, feet hip-width apart.", "Bend hips and knees to grip bar with overhand grip.", "Pull bar up with explosive movement bringing it to shoulders.", "From shoulders, push bar up until arms are extended.", "Lower bar to shoulders then to floor in controlled manner."]'::jsonb),

('80000000-0001-0000-0000-000000000003', 'en', 'Thrusters', 'thrusters squat press',
'["Hold bar in front squat position or dumbbells at shoulder height.", "Perform deep squat keeping chest up.", "As you rise, use momentum to push weight overhead.", "Lower weight to shoulders while descending into next squat.", "Movement should be fluid and continuous."]'::jsonb),

('80000000-0001-0000-0000-000000000004', 'en', 'Turkish Get-Up', 'turkish get up kettlebell',
'["Lie on back holding kettlebell with one arm extended toward ceiling.", "Bend knee on same side and place foot on floor.", "Raise torso supporting on opposite elbow, then hand.", "Lift hips off floor and sweep free leg under to kneel.", "Stand up keeping weight overhead. Reverse movement to lower."]'::jsonb),

('80000000-0001-0000-0000-000000000005', 'en', 'Kettlebell Swing', 'kettlebell swing hip hinge',
'["Stand with feet wider than shoulders, kettlebell on floor in front.", "Hinge at hips to grip kettlebell with both hands.", "Swing kettlebell between legs then drive forward with hips.", "Bring kettlebell to shoulder height with arms extended.", "Let kettlebell swing down and repeat in fluid motion."]'::jsonb),

('80000000-0001-0000-0000-000000000006', 'en', 'Barbell Clean', 'barbell clean power',
'["Place bar on floor with feet hip-width apart.", "Bend hips and knees to grip bar with overhand grip.", "Pull bar up explosively using hips and legs.", "Get under bar rotating elbows to receive it on shoulders.", "Stand fully then lower bar in controlled manner."]'::jsonb),

('80000000-0001-0000-0000-000000000007', 'en', 'Barbell Snatch', 'barbell snatch olympic lift',
'["Place bar on floor and grip with wide grip.", "Bend hips and knees keeping back straight.", "Pull bar up explosively, bringing it directly overhead.", "Get under bar in squat position with arms locked.", "Stand up completing the lift."]'::jsonb),

('80000000-0001-0000-0000-000000000008', 'en', 'Man Makers', 'man makers dumbbell row pushup',
'["Start in plank position with dumbbell in each hand.", "Perform push-up.", "Do row with one arm, then other.", "Jump feet toward hands.", "Stand and perform clean and press with dumbbells.", "Lower dumbbells and return to plank."]'::jsonb),

('80000000-0001-0000-0000-000000000009', 'en', 'Box Jumps', 'box jumps plyometric',
'["Stand facing sturdy box or bench.", "Bend knees and swing arms back.", "Jump explosively up and forward landing on box.", "Land softly with both feet and knees slightly bent.", "Step or jump down from box and repeat."]'::jsonb),

('80000000-0001-0000-0000-000000000010', 'en', 'Renegade Rows', 'renegade rows plank row',
'["Get in plank position with dumbbell in each hand.", "Keep core tight and hips stable.", "Row one arm bringing dumbbell toward hip.", "Lower dumbbell and repeat with other arm.", "Avoid rotating hips during movement."]'::jsonb),

('80000000-0001-0000-0000-000000000011', 'en', 'Jump Squat', 'jump squat plyometric',
'["Stand with feet shoulder-width apart.", "Lower into deep squat with arms back.", "Explode up jumping as high as possible.", "Land softly going directly into next squat.", "Repeat continuously maintaining good form."]'::jsonb),

('80000000-0001-0000-0000-000000000012', 'en', 'Wall Balls', 'wall balls medicine ball functional',
'["Stand facing wall holding medicine ball against chest.", "Perform deep squat.", "As you rise, push ball up hitting wall at marked height.", "Catch ball on descent and lower directly into next squat.", "Maintain consistent rhythm."]'::jsonb),

('80000000-0001-0000-0000-000000000013', 'en', 'Mountain Climbers', 'mountain climbers cardio core',
'["Get in high plank position with hands under shoulders.", "Bring one knee toward chest keeping core engaged.", "Return leg to starting position while bringing other knee to chest.", "Alternate legs quickly as if running in place.", "Keep hips low and body in straight line."]'::jsonb),

('80000000-0001-0000-0000-000000000014', 'en', 'Dumbbell Clean', 'dumbbell clean power',
'["Stand with dumbbell in each hand in front of thighs.", "Slightly bend knees and hips.", "Pull dumbbells up explosively using hips.", "Rotate wrists to receive dumbbells at shoulders.", "Lower dumbbells in controlled manner and repeat."]'::jsonb),

('80000000-0001-0000-0000-000000000015', 'en', 'Devil Press', 'devil press dumbbell burpee snatch',
'["Start standing with dumbbells on floor.", "Lower into push-up with hands on dumbbells.", "Perform push-up and jump feet toward dumbbells.", "Swing dumbbells between legs and bring overhead.", "Lower dumbbells to floor and repeat."]'::jsonb),

('80000000-0001-0000-0000-000000000016', 'en', 'Sled Push', 'sled push prowler',
'["Position yourself behind sled with hands on handles.", "Lean torso forward and engage core.", "Push sled walking or running forward.", "Keep arms extended and push with legs.", "Continue for indicated distance or time."]'::jsonb),

('80000000-0001-0000-0000-000000000017', 'en', 'Battle Ropes', 'battle ropes waves conditioning',
'["Stand with feet shoulder-width apart holding end of each rope.", "Slightly bend knees and engage core.", "Move arms up and down alternately creating waves in ropes.", "Maintain fast and steady rhythm.", "Can vary with simultaneous waves, circles or slams."]'::jsonb),

('80000000-0001-0000-0000-000000000018', 'en', 'Lunge with Press', 'lunge with press dumbbell overhead',
'["Stand holding dumbbells at shoulder height.", "Step forward and lower into lunge.", "As you rise, press dumbbells overhead.", "Lower dumbbells while returning to starting position.", "Alternate legs with each rep."]'::jsonb),

('80000000-0001-0000-0000-000000000019', 'en', 'Squat Curl Press', 'squat curl press compound',
'["Stand holding dumbbells at sides.", "Perform deep squat.", "As you rise, curl dumbbells.", "Continue movement pressing dumbbells overhead.", "Lower dumbbells and repeat."]'::jsonb),

('80000000-0001-0000-0000-000000000020', 'en', 'Hang Clean', 'hang clean barbell power',
'["Stand holding bar at hip height with overhand grip.", "Slightly bend knees and hips bringing bar just above knees.", "Pull explosively using hips and get under bar.", "Receive bar on shoulders in front squat position.", "Stand up and lower bar to starting position."]'::jsonb),

('80000000-0001-0000-0000-000000000021', 'en', 'Dumbbell Snatch', 'dumbbell snatch single arm',
'["Stand with feet shoulder-width apart, one dumbbell between feet.", "Bend hips and knees to grip dumbbell with one hand.", "Pull dumbbell up explosively extending hips and knees.", "Receive dumbbell overhead with arm extended.", "Lower in controlled manner and repeat. Switch arms."]'::jsonb),

('80000000-0001-0000-0000-000000000022', 'en', 'Farmer Walk', 'farmer walk carry loaded',
'["Stand holding heavy dumbbell or kettlebell in each hand.", "Keep core engaged and shoulders back.", "Walk in controlled manner for set distance.", "Maintain upright posture without leaning.", "This exercise works grip strength, core and stability."]'::jsonb),

('80000000-0001-0000-0000-000000000023', 'en', 'Barbell Jerk', 'barbell jerk olympic lift',
'["Start with bar on shoulders in front rack position.", "Slightly bend knees to generate drive.", "Explosively extend legs while pushing bar up.", "Split legs or bend to get under bar.", "Recover to standing position with arms locked overhead."]'::jsonb),

('80000000-0001-0000-0000-000000000024', 'en', 'Rope Climb', 'rope climb functional',
'["Stand under rope with hands as high as possible.", "Secure rope with feet using wrap or step technique.", "Pull with arms while pushing with legs.", "Repeat process until reaching top.", "Descend in controlled manner using arms and legs."]'::jsonb),

('80000000-0001-0000-0000-000000000025', 'en', 'Bear Crawl', 'bear crawl quadruped locomotion',
'["Get on all fours with knees slightly elevated off floor.", "Move forward by moving right hand and left foot at same time.", "Then move left hand and right foot.", "Keep back flat and hips low.", "Move for indicated distance or time."]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '80000000-0001-%'
GROUP BY language_code;
