INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('70000000-0001-0000-0000-000000000001', 'es', 'Sentadilla con barra (Back Squat)', 'sentadilla con barra back squat',
'["Colocá la barra sobre los trapecios superiores y separá los pies al ancho de los hombros.", "Mantené el pecho arriba, la espalda recta y el core activado.", "Flexioná caderas y rodillas para descender hasta que los muslos estén paralelos al suelo o más abajo.", "Empujá a través de los talones para volver a la posición inicial.", "Evitá que las rodillas colapsen hacia adentro durante el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000002', 'es', 'Sentadilla frontal', 'sentadilla frontal front squat',
'["Colocá la barra sobre los deltoides anteriores con los codos altos.", "Separá los pies al ancho de los hombros.", "Descendé manteniendo el torso lo más vertical posible.", "Bajá hasta que los muslos estén paralelos al suelo o más abajo.", "Empujá a través de los talones para subir manteniendo los codos altos."]'::jsonb),

('70000000-0001-0000-0000-000000000003', 'es', 'Prensa de piernas', 'prensa de piernas leg press',
'["Sentate en la máquina con la espalda apoyada y los pies en la plataforma al ancho de los hombros.", "Liberá los seguros y bajá la plataforma flexionando las rodillas.", "Descendé hasta que las rodillas formen un ángulo de 90 grados.", "Empujá la plataforma hasta extender las piernas sin bloquear las rodillas.", "Mantené la zona lumbar pegada al respaldo durante todo el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000004', 'es', 'Extensiones de cuádriceps', 'extensiones de cuadriceps leg extension',
'["Sentate en la máquina con la espalda apoyada y el rodillo sobre los tobillos.", "Ajustá el respaldo para que las rodillas estén alineadas con el eje de la máquina.", "Extendé las piernas contrayendo los cuádriceps.", "Mantené la contracción un segundo en la parte alta.", "Bajá de forma controlada sin dejar que el peso descanse."]'::jsonb),

('70000000-0001-0000-0000-000000000005', 'es', 'Curl de piernas acostado', 'curl de piernas acostado lying leg curl hamstring',
'["Acostate boca abajo en la máquina con el rodillo sobre los talones.", "Flexioná las rodillas llevando los talones hacia los glúteos.", "Contraé los isquiotibiales en la parte alta del movimiento.", "Bajá de forma controlada sin extender completamente las rodillas.", "Evitá levantar las caderas durante el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000006', 'es', 'Curl de piernas sentado', 'curl de piernas sentado seated leg curl hamstring',
'["Sentate en la máquina con la espalda apoyada y el rodillo sobre los talones.", "Ajustá la almohadilla del muslo para fijar las piernas.", "Flexioná las rodillas llevando los talones debajo del asiento.", "Contraé los isquiotibiales y volvé de forma controlada.", "Mantené las caderas fijas durante todo el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000007', 'es', 'Peso muerto rumano con mancuernas', 'peso muerto rumano con mancuernas rdl dumbbell',
'["Parate con los pies al ancho de las caderas sosteniendo una mancuerna en cada mano.", "Mantené las rodillas ligeramente flexionadas y fijas.", "Flexioná las caderas llevando las mancuernas hacia abajo por delante de las piernas.", "Bajá hasta sentir un estiramiento en los isquiotibiales.", "Volvé contrayendo glúteos y manteniendo la espalda recta."]'::jsonb),

('70000000-0001-0000-0000-000000000008', 'es', 'Estocadas (Lunges) con mancuernas', 'estocadas lunges con mancuernas',
'["Parate con una mancuerna en cada mano a los costados.", "Dá un paso largo hacia adelante y flexioná ambas rodillas.", "Bajá hasta que la rodilla trasera casi toque el suelo.", "Empujá con el pie delantero para volver a la posición inicial.", "Alterná las piernas con cada repetición."]'::jsonb),

('70000000-0001-0000-0000-000000000009', 'es', 'Sentadilla búlgara', 'sentadilla bulgara split squat',
'["Parate de espaldas a un banco con un pie apoyado sobre él.", "Sostené mancuernas a los costados o una barra sobre los hombros.", "Flexioná la rodilla delantera para descender hasta que el muslo esté paralelo al suelo.", "Empujá con el pie delantero para volver a subir.", "Completá las repeticiones y cambiá de pierna."]'::jsonb),

('70000000-0001-0000-0000-000000000010', 'es', 'Elevaciones de talones de pie', 'elevaciones de talones de pie standing calf raise',
'["Parate en una máquina de gemelos o en un escalón con los talones colgando.", "Colocá los hombros bajo las almohadillas si usás máquina.", "Elevá los talones lo más alto posible contrayendo los gemelos.", "Mantené la contracción un segundo.", "Bajá de forma controlada estirando los gemelos al máximo."]'::jsonb),

('70000000-0001-0000-0000-000000000011', 'es', 'Elevaciones de talones sentado', 'elevaciones de talones sentado seated calf raise',
'["Sentate en la máquina con las rodillas bajo las almohadillas y los pies sobre la plataforma.", "Liberá el seguro y bajá los talones para estirar los gemelos.", "Elevá los talones lo más alto posible.", "Mantené la contracción un segundo.", "Bajá de forma controlada."]'::jsonb),

('70000000-0001-0000-0000-000000000012', 'es', 'Sentadilla Hack', 'sentadilla hack hack squat',
'["Colocate en la máquina hack con la espalda contra el respaldo.", "Posicioná los pies en la plataforma al ancho de los hombros.", "Liberá los seguros y bajá flexionando las rodillas.", "Descendé hasta que los muslos estén paralelos a la plataforma.", "Empujá para subir sin bloquear las rodillas."]'::jsonb),

('70000000-0001-0000-0000-000000000013', 'es', 'Hip thrust con barra', 'hip thrust con barra gluteos',
'["Sentate en el suelo con la espalda apoyada en un banco y la barra sobre las caderas.", "Los pies deben estar apoyados en el suelo, rodillas flexionadas.", "Empujá las caderas hacia arriba hasta que el torso quede paralelo al suelo.", "Contraé fuertemente los glúteos en la parte alta.", "Bajá de forma controlada sin tocar el suelo con los glúteos."]'::jsonb),

('70000000-0001-0000-0000-000000000014', 'es', 'Sentadilla goblet', 'sentadilla goblet copa kettlebell',
'["Sostené una mancuerna o kettlebell contra el pecho con ambas manos.", "Separá los pies al ancho de los hombros o un poco más.", "Descendé empujando las caderas hacia atrás y abajo.", "Mantené el pecho arriba y los codos entre las rodillas.", "Empujá a través de los talones para subir."]'::jsonb),

('70000000-0001-0000-0000-000000000015', 'es', 'Aducción de cadera en máquina', 'aduccion de cadera en maquina adductor',
'["Sentate en la máquina con las piernas separadas y las almohadillas en la parte interna de los muslos.", "Juntá las piernas llevando las rodillas una hacia la otra.", "Contraé los aductores en el punto más cerrado.", "Volvé de forma controlada a la posición inicial.", "Evitá usar impulso durante el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000016', 'es', 'Abducción de cadera en máquina', 'abduccion de cadera en maquina abductor',
'["Sentate en la máquina con las piernas juntas y las almohadillas en la parte externa de los muslos.", "Separá las piernas llevando las rodillas hacia afuera.", "Contraé los abductores en el punto más abierto.", "Volvé de forma controlada a la posición inicial.", "Mantené la espalda apoyada durante todo el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000017', 'es', 'Step-ups', 'step ups subidas al banco',
'["Parate frente a un banco o caja sosteniendo mancuernas a los costados.", "Colocá un pie completamente sobre el banco.", "Empujá con esa pierna para subir el cuerpo sobre el banco.", "Bajá de forma controlada con la misma pierna.", "Completá las repeticiones con una pierna antes de cambiar."]'::jsonb),

('70000000-0001-0000-0000-000000000018', 'es', 'Sentadilla sumo', 'sentadilla sumo piernas separadas',
'["Parate con los pies muy separados y las puntas hacia afuera.", "Sostené una mancuerna o kettlebell entre las piernas.", "Descendé flexionando las rodillas hacia los lados.", "Bajá hasta que los muslos estén paralelos al suelo.", "Empujá a través de los talones para subir."]'::jsonb),

('70000000-0001-0000-0000-000000000019', 'es', 'Glute bridge', 'glute bridge puente de gluteos',
'["Acostate boca arriba con las rodillas flexionadas y los pies apoyados en el suelo.", "Los brazos deben estar a los costados con las palmas hacia abajo.", "Empujá las caderas hacia arriba contrayendo los glúteos.", "Formá una línea recta desde los hombros hasta las rodillas.", "Bajá de forma controlada sin tocar el suelo."]'::jsonb),

('70000000-0001-0000-0000-000000000020', 'es', 'Patada de glúteo en máquina', 'patada de gluteo en maquina kickback',
'["Colocate en la máquina con una rodilla en la plataforma y el pie contra el pedal.", "Empujá el pedal hacia atrás extendiendo la cadera.", "Contraé el glúteo en la parte alta del movimiento.", "Volvé de forma controlada sin dejar que el peso descanse.", "Completá las repeticiones y cambiá de pierna."]'::jsonb),

('70000000-0001-0000-0000-000000000021', 'es', 'Nordic curl', 'nordic curl isquiotibiales nordico',
'["Arrodillate con los tobillos fijados bajo un soporte o con ayuda de un compañero.", "Mantené el cuerpo recto desde las rodillas hasta la cabeza.", "Bajá el cuerpo hacia adelante de forma controlada usando los isquiotibiales.", "Bajá lo más que puedas y usá las manos para ayudarte a volver.", "Este ejercicio es muy exigente; progresá gradualmente."]'::jsonb),

('70000000-0001-0000-0000-000000000022', 'es', 'Sentadilla pistol', 'sentadilla pistol una pierna single leg squat',
'["Parate sobre una pierna con la otra extendida hacia adelante.", "Extendé los brazos al frente para equilibrarte.", "Flexioná la rodilla de apoyo para descender lo más bajo posible.", "Mantené la pierna extendida sin tocar el suelo.", "Empujá con la pierna de apoyo para volver a subir."]'::jsonb),

('70000000-0001-0000-0000-000000000023', 'es', 'Sentadilla en máquina Smith', 'sentadilla en maquina smith',
'["Colocate bajo la barra de la máquina Smith con los pies ligeramente adelantados.", "Posicioná la barra sobre los trapecios y liberá los seguros.", "Descendé flexionando las rodillas hasta que los muslos estén paralelos.", "Empujá para subir manteniendo la espalda recta.", "La guía de la máquina proporciona estabilidad adicional."]'::jsonb),

('70000000-0001-0000-0000-000000000024', 'es', 'Peso muerto con piernas rígidas', 'peso muerto con piernas rigidas stiff leg deadlift',
'["Parate con los pies al ancho de las caderas sosteniendo una barra.", "Mantené las piernas casi completamente rectas durante todo el movimiento.", "Flexioná las caderas llevando la barra hacia abajo.", "Bajá hasta sentir un estiramiento intenso en los isquiotibiales.", "Volvé contrayendo glúteos sin redondear la espalda."]'::jsonb),

('70000000-0001-0000-0000-000000000025', 'es', 'Buenos días (Good mornings)', 'buenos dias good mornings',
'["Colocá una barra sobre los trapecios como en una sentadilla.", "Separá los pies al ancho de los hombros con rodillas ligeramente flexionadas.", "Flexioná las caderas llevando el torso hacia adelante.", "Mantené la espalda recta durante todo el movimiento.", "Bajá hasta que el torso esté casi paralelo al suelo y volvé."]'::jsonb),

('70000000-0001-0000-0000-000000000026', 'es', 'Estocada caminando', 'estocada caminando walking lunge',
'["Parate con mancuernas a los costados o barra sobre los hombros.", "Dá un paso largo hacia adelante y bajá la rodilla trasera hacia el suelo.", "En lugar de volver atrás, llevá el pie trasero hacia adelante para el siguiente paso.", "Continuá caminando alternando las piernas.", "Mantené el torso erguido durante todo el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000027', 'es', 'Curl de piernas de pie', 'curl de piernas de pie standing leg curl',
'["Parate en la máquina con una pierna fija y el tobillo de la otra bajo el rodillo.", "Sostente de las empuñaduras para mantener el equilibrio.", "Flexioná la rodilla llevando el talón hacia el glúteo.", "Contraé el isquiotibial y bajá de forma controlada.", "Completá las repeticiones y cambiá de pierna."]'::jsonb),

('70000000-0001-0000-0000-000000000028', 'es', 'Elevación de talones en prensa', 'elevacion de talones en prensa calf raise leg press',
'["Colocate en la prensa de piernas con solo las puntas de los pies en la plataforma.", "Liberá los seguros y extendé las piernas casi completamente.", "Bajá los talones para estirar los gemelos.", "Empujá con las puntas de los pies para elevar los talones.", "Mantené las piernas casi rectas durante todo el movimiento."]'::jsonb),

('70000000-0001-0000-0000-000000000029', 'es', 'Patada de glúteo en polea', 'patada de gluteo en polea cable kickback',
'["Conectá una tobillera a la polea baja y colocala en un tobillo.", "Parate frente a la máquina sosteniéndote de algo estable.", "Extendé la pierna hacia atrás sin arquear la espalda.", "Contraé el glúteo en la parte alta del movimiento.", "Volvé de forma controlada y repetí. Cambiá de pierna."]'::jsonb),

('70000000-0001-0000-0000-000000000030', 'es', 'Sentadilla con banda elástica', 'sentadilla con banda elastica resistance band squat',
'["Colocá una banda de resistencia justo arriba de las rodillas.", "Separá los pies al ancho de los hombros y empujá las rodillas hacia afuera.", "Descendé como en una sentadilla normal manteniendo tensión en la banda.", "Mantené las rodillas alineadas con los pies sin dejarlas colapsar.", "Empujá a través de los talones para subir."]'::jsonb);

