INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('70000000-0001-0000-0000-000000000001', 'en', 'Barbell Back Squat', 'barbell back squat',
'["Place bar on upper traps and separate feet shoulder-width apart.", "Keep chest up, back straight and core engaged.", "Bend hips and knees to descend until thighs are parallel to floor or below.", "Push through heels to return to starting position.", "Avoid letting knees cave inward during movement."]'::jsonb),

('70000000-0001-0000-0000-000000000002', 'en', 'Front Squat', 'front squat barbell',
'["Place bar on front delts with elbows high.", "Separate feet shoulder-width apart.", "Descend keeping torso as vertical as possible.", "Lower until thighs are parallel to floor or below.", "Push through heels to rise keeping elbows high."]'::jsonb),

('70000000-0001-0000-0000-000000000003', 'en', 'Leg Press', 'leg press machine',
'["Sit in machine with back supported and feet on platform shoulder-width apart.", "Release locks and lower platform by bending knees.", "Descend until knees form 90-degree angle.", "Push platform until legs are extended without locking knees.", "Keep lower back against backrest throughout movement."]'::jsonb),

('70000000-0001-0000-0000-000000000004', 'en', 'Leg Extension', 'leg extension machine quadriceps',
'["Sit in machine with back supported and roller on ankles.", "Adjust backrest so knees align with machine axis.", "Extend legs contracting quads.", "Hold contraction for a second at top.", "Lower in controlled manner without letting weight rest."]'::jsonb),

('70000000-0001-0000-0000-000000000005', 'en', 'Lying Leg Curl', 'lying leg curl hamstring machine',
'["Lie face down on machine with roller on heels.", "Bend knees bringing heels toward glutes.", "Contract hamstrings at top of movement.", "Lower in controlled manner without fully extending knees.", "Avoid lifting hips during movement."]'::jsonb),

('70000000-0001-0000-0000-000000000006', 'en', 'Seated Leg Curl', 'seated leg curl hamstring machine',
'["Sit in machine with back supported and roller on heels.", "Adjust thigh pad to fix legs.", "Bend knees bringing heels under seat.", "Contract hamstrings and return in controlled manner.", "Keep hips fixed throughout movement."]'::jsonb),

('70000000-0001-0000-0000-000000000007', 'en', 'Dumbbell Romanian Deadlift', 'dumbbell romanian deadlift rdl',
'["Stand with feet hip-width apart holding dumbbell in each hand.", "Keep knees slightly bent and fixed.", "Hinge at hips lowering dumbbells down front of legs.", "Lower until you feel hamstring stretch.", "Return contracting glutes and keeping back straight."]'::jsonb),

('70000000-0001-0000-0000-000000000008', 'en', 'Dumbbell Lunges', 'dumbbell lunges walking',
'["Stand with dumbbell in each hand at sides.", "Take long step forward and bend both knees.", "Lower until back knee nearly touches floor.", "Push with front foot to return to starting position.", "Alternate legs with each rep."]'::jsonb),

('70000000-0001-0000-0000-000000000009', 'en', 'Bulgarian Split Squat', 'bulgarian split squat rear foot elevated',
'["Stand facing away from bench with one foot resting on it.", "Hold dumbbells at sides or bar on shoulders.", "Bend front knee to descend until thigh is parallel to floor.", "Push with front foot to rise.", "Complete reps and switch legs."]'::jsonb),

('70000000-0001-0000-0000-000000000010', 'en', 'Standing Calf Raise', 'standing calf raise machine',
'["Stand on calf machine or step with heels hanging off.", "Place shoulders under pads if using machine.", "Raise heels as high as possible contracting calves.", "Hold contraction for a second.", "Lower in controlled manner stretching calves fully."]'::jsonb),

('70000000-0001-0000-0000-000000000011', 'en', 'Seated Calf Raise', 'seated calf raise machine',
'["Sit in machine with knees under pads and feet on platform.", "Release lock and lower heels to stretch calves.", "Raise heels as high as possible.", "Hold contraction for a second.", "Lower in controlled manner."]'::jsonb),

('70000000-0001-0000-0000-000000000012', 'en', 'Hack Squat', 'hack squat machine',
'["Position yourself in hack machine with back against pad.", "Place feet on platform shoulder-width apart.", "Release locks and lower by bending knees.", "Descend until thighs are parallel to platform.", "Push to rise without locking knees."]'::jsonb),

('70000000-0001-0000-0000-000000000013', 'en', 'Barbell Hip Thrust', 'barbell hip thrust glutes',
'["Sit on floor with back against bench and bar over hips.", "Feet should be on floor, knees bent.", "Push hips up until torso is parallel to floor.", "Strongly contract glutes at top.", "Lower in controlled manner without glutes touching floor."]'::jsonb),

('70000000-0001-0000-0000-000000000014', 'en', 'Goblet Squat', 'goblet squat kettlebell dumbbell',
'["Hold dumbbell or kettlebell against chest with both hands.", "Separate feet shoulder-width apart or slightly wider.", "Descend pushing hips back and down.", "Keep chest up and elbows between knees.", "Push through heels to rise."]'::jsonb),

('70000000-0001-0000-0000-000000000015', 'en', 'Hip Adduction Machine', 'hip adduction machine inner thigh',
'["Sit in machine with legs apart and pads on inner thighs.", "Bring legs together pushing knees toward each other.", "Contract adductors at closest point.", "Return in controlled manner to starting position.", "Avoid using momentum during movement."]'::jsonb),

('70000000-0001-0000-0000-000000000016', 'en', 'Hip Abduction Machine', 'hip abduction machine outer thigh',
'["Sit in machine with legs together and pads on outer thighs.", "Separate legs pushing knees outward.", "Contract abductors at widest point.", "Return in controlled manner to starting position.", "Keep back supported throughout movement."]'::jsonb),

('70000000-0001-0000-0000-000000000017', 'en', 'Step-Ups', 'step ups box bench',
'["Stand facing bench or box holding dumbbells at sides.", "Place one foot completely on bench.", "Push with that leg to raise body onto bench.", "Lower in controlled manner with same leg.", "Complete reps with one leg before switching."]'::jsonb),

('70000000-0001-0000-0000-000000000018', 'en', 'Sumo Squat', 'sumo squat wide stance',
'["Stand with feet wide apart and toes pointed out.", "Hold dumbbell or kettlebell between legs.", "Descend bending knees to sides.", "Lower until thighs are parallel to floor.", "Push through heels to rise."]'::jsonb),

('70000000-0001-0000-0000-000000000019', 'en', 'Glute Bridge', 'glute bridge floor',
'["Lie on back with knees bent and feet on floor.", "Arms should be at sides with palms down.", "Push hips up contracting glutes.", "Form straight line from shoulders to knees.", "Lower in controlled manner without touching floor."]'::jsonb),

('70000000-0001-0000-0000-000000000020', 'en', 'Glute Kickback Machine', 'glute kickback machine',
'["Position yourself in machine with one knee on platform and foot against pedal.", "Push pedal back extending hip.", "Contract glute at top of movement.", "Return in controlled manner without letting weight rest.", "Complete reps and switch legs."]'::jsonb),

('70000000-0001-0000-0000-000000000021', 'en', 'Nordic Curl', 'nordic curl hamstring',
'["Kneel with ankles fixed under support or with partner assistance.", "Keep body straight from knees to head.", "Lower body forward in controlled manner using hamstrings.", "Lower as far as possible and use hands to help return.", "This exercise is very demanding; progress gradually."]'::jsonb),

('70000000-0001-0000-0000-000000000022', 'en', 'Pistol Squat', 'pistol squat single leg',
'["Stand on one leg with other extended forward.", "Extend arms forward for balance.", "Bend supporting knee to descend as low as possible.", "Keep extended leg from touching floor.", "Push with supporting leg to rise."]'::jsonb),

('70000000-0001-0000-0000-000000000023', 'en', 'Smith Machine Squat', 'smith machine squat',
'["Position yourself under Smith machine bar with feet slightly forward.", "Place bar on traps and release locks.", "Descend bending knees until thighs are parallel.", "Push to rise keeping back straight.", "Machine guide provides additional stability."]'::jsonb),

('70000000-0001-0000-0000-000000000024', 'en', 'Stiff Leg Deadlift', 'stiff leg deadlift straight',
'["Stand with feet hip-width apart holding bar.", "Keep legs almost completely straight throughout movement.", "Hinge at hips lowering bar down.", "Lower until you feel intense hamstring stretch.", "Return contracting glutes without rounding back."]'::jsonb),

('70000000-0001-0000-0000-000000000025', 'en', 'Good Mornings', 'good mornings barbell',
'["Place bar on traps as in a squat.", "Separate feet shoulder-width apart with knees slightly bent.", "Hinge at hips bringing torso forward.", "Keep back straight throughout movement.", "Lower until torso is nearly parallel to floor and return."]'::jsonb),

('70000000-0001-0000-0000-000000000026', 'en', 'Walking Lunge', 'walking lunge dumbbell barbell',
'["Stand with dumbbells at sides or bar on shoulders.", "Take long step forward and lower back knee toward floor.", "Instead of stepping back, bring rear foot forward for next step.", "Continue walking alternating legs.", "Keep torso upright throughout movement."]'::jsonb),

('70000000-0001-0000-0000-000000000027', 'en', 'Standing Leg Curl', 'standing leg curl machine single',
'["Stand in machine with one leg fixed and other ankle under roller.", "Hold handles to maintain balance.", "Bend knee bringing heel toward glute.", "Contract hamstring and lower in controlled manner.", "Complete reps and switch legs."]'::jsonb),

('70000000-0001-0000-0000-000000000028', 'en', 'Calf Raise on Leg Press', 'calf raise leg press machine',
'["Position yourself in leg press with only balls of feet on platform.", "Release locks and extend legs almost completely.", "Lower heels to stretch calves.", "Push with balls of feet to raise heels.", "Keep legs almost straight throughout movement."]'::jsonb),

('70000000-0001-0000-0000-000000000029', 'en', 'Cable Glute Kickback', 'cable glute kickback ankle strap',
'["Attach ankle strap to low pulley and place on ankle.", "Stand facing machine holding something stable.", "Extend leg back without arching back.", "Contract glute at top of movement.", "Return in controlled manner and repeat. Switch legs."]'::jsonb),

('70000000-0001-0000-0000-000000000030', 'en', 'Resistance Band Squat', 'resistance band squat banded',
'["Place resistance band just above knees.", "Separate feet shoulder-width apart and push knees out.", "Descend as in normal squat maintaining band tension.", "Keep knees aligned with feet without letting them cave.", "Push through heels to rise."]'::jsonb);

