-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: HOMBROS
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('20000000-0001-0000-0000-000000000001', 'es', 'Press militar con barra', 'press militar con barra overhead',
'["Colocate de pie con los pies al ancho de los hombros y la barra apoyada sobre la parte superior del pecho.", "Tomá la barra con agarre pronado, un poco más ancho que los hombros.", "Activá el core y mantené una ligera retracción escapular.", "Empujá la barra hacia arriba hasta extender completamente los brazos sin bloquear los codos.", "Descendé la barra de forma controlada hasta la posición inicial, sin perder tensión.", "Evitá arquear excesivamente la espalda baja durante el movimiento."]'::jsonb),

('20000000-0001-0000-0000-000000000002', 'es', 'Press militar con mancuernas', 'press militar con mancuernas dumbbell',
'["Sentate en un banco con respaldo y apoyá los pies firmes en el suelo.", "Sostené una mancuerna en cada mano a la altura de los hombros con agarre pronado.", "Mantené la espalda recta y los omóplatos retraídos.", "Empujá las mancuernas hacia arriba hasta extender completamente los brazos.", "Descendé las mancuernas controladamente hasta la altura inicial.", "Evitá juntar las mancuernas arriba o arquear la espalda durante el movimiento."]'::jsonb),

('20000000-0001-0000-0000-000000000003', 'es', 'Press Arnold', 'press arnold mancuernas',
'["Sentate en un banco con respaldo y sostené una mancuerna en cada mano frente al pecho, con las palmas mirando hacia vos.", "Iniciá el movimiento girando las muñecas hacia afuera mientras empujás las mancuernas hacia arriba.", "Terminá con los brazos extendidos y las palmas mirando hacia adelante.", "Descendé controladamente revirtiendo el giro hasta volver a la posición inicial.", "Mantené el abdomen firme y evitá arquear la espalda baja."]'::jsonb),

('20000000-0001-0000-0000-000000000004', 'es', 'Elevaciones laterales con mancuernas', 'elevaciones laterales con mancuernas lateral raise',
'["Parate derecho con una mancuerna en cada mano a los costados del cuerpo, palmas hacia adentro.", "Con los codos ligeramente flexionados, elevá los brazos hacia los costados hasta que queden paralelos al suelo.", "Mantené un segundo en la parte alta del movimiento.", "Descendé lentamente hasta la posición inicial sin balancearte.", "Evitá usar impulso o encoger los hombros."]'::jsonb),

('20000000-0001-0000-0000-000000000005', 'es', 'Elevaciones frontales con mancuernas', 'elevaciones frontales con mancuernas front raise',
'["De pie, sostené una mancuerna en cada mano frente a los muslos con las palmas hacia el cuerpo.", "Elevá un brazo al frente hasta la altura de los hombros manteniendo el codo ligeramente flexionado.", "Descendé controladamente y repetí con el otro brazo o ambos a la vez.", "Evitá balancearte o arquear la espalda."]'::jsonb),

('20000000-0001-0000-0000-000000000006', 'es', 'Elevaciones laterales con polea', 'elevaciones laterales con polea cable',
'["Parate al costado de la máquina de polea con el cable ajustado en la posición más baja.", "Tomá el asa con la mano más alejada y mantené el brazo levemente flexionado.", "Elevá el brazo lateralmente hasta la altura del hombro manteniendo el control del movimiento.", "Bajá lentamente hasta la posición inicial.", "Evitá girar el torso o usar impulso."]'::jsonb),

('20000000-0001-0000-0000-000000000007', 'es', 'Remo al mentón con barra', 'remo al menton con barra upright row',
'["De pie, sostené una barra con agarre pronado y manos al ancho de los hombros.", "Tirando de los codos hacia arriba, llevá la barra cerca del cuerpo hasta la altura del mentón.", "Mantené las muñecas rectas y los codos más altos que las manos.", "Descendé lentamente hasta la posición inicial.", "Evitá encoger los hombros o usar impulso."]'::jsonb),

('20000000-0001-0000-0000-000000000008', 'es', 'Remo al mentón con polea', 'remo al menton con polea cable upright row',
'["Conectá una barra recta a la polea baja y tomala con agarre pronado.", "De pie, tirá de la barra hacia arriba guiando con los codos hasta que llegue cerca del mentón.", "Mantené el torso erguido y el core firme.", "Descendé lentamente hasta extender los brazos completamente.", "Evitá usar impulso o elevar los hombros."]'::jsonb),

('20000000-0001-0000-0000-000000000009', 'es', 'Elevaciones posteriores (pájaros) con mancuernas', 'elevaciones posteriores pajaros rear delt fly',
'["Con una mancuerna en cada mano, incliná el torso hacia adelante hasta casi quedar paralelo al suelo.", "Dejá los brazos colgando con los codos ligeramente flexionados.", "Elevá los brazos hacia los costados hasta la altura de los hombros, contrayendo los deltoides posteriores.", "Bajá lentamente sin perder tensión.", "Evitá balancearte o extender el tronco durante el movimiento."]'::jsonb),

('20000000-0001-0000-0000-000000000010', 'es', 'Face Pull con polea', 'face pull con polea cable',
'["Ajustá la polea a la altura del rostro y acoplá una cuerda.", "Tomá la cuerda con agarre neutro y retrocedé unos pasos para generar tensión.", "Tirá de la cuerda hacia tu cara separando las manos al final del movimiento.", "Mantené los codos altos y las escápulas retraídas.", "Volvé lentamente a la posición inicial sin perder tensión."]'::jsonb),

('20000000-0001-0000-0000-000000000011', 'es', 'Press de hombros en máquina', 'press de hombros en maquina shoulder press machine',
'["Ajustá el asiento para que las empuñaduras queden a la altura de los hombros.", "Sentate con la espalda apoyada y los pies firmes en el suelo.", "Tomá las empuñaduras con agarre pronado.", "Empujá hacia arriba hasta extender completamente los brazos.", "Descendé controladamente hasta que las empuñaduras queden cerca de los hombros.", "Evitá despegar la espalda del respaldo."]'::jsonb),

('20000000-0001-0000-0000-000000000012', 'es', 'Plancha con empuje escapular', 'plancha con empuje escapular scapular push up',
'["Colocate en posición de plancha alta con las manos debajo de los hombros y el cuerpo en línea recta.", "Sin doblar los codos, empujá el suelo con las manos para separar los omóplatos (protracción escapular).", "Mantené la posición un segundo, luego permití que los omóplatos se junten nuevamente (retracción).", "Repetí el movimiento controladamente sin perder la alineación corporal.", "Evitá hundir la zona lumbar o flexionar los codos."]'::jsonb),

('20000000-0001-0000-0000-000000000013', 'es', 'Push press con barra', 'push press con barra',
'["Parate con los pies al ancho de los hombros y sostené la barra a la altura de los hombros con agarre pronado", "Activá el core y mantené el pecho erguido con los codos ligeramente por delante de la barra", "Flexioná levemente las rodillas y usá un pequeño impulso de piernas para iniciar el movimiento", "Extendé caderas y rodillas mientras empujás la barra hacia arriba hasta extender completamente los brazos", "Descendé controladamente la barra hasta la posición inicial sobre los hombros y repetí", "Evitá arquear la espalda y mantené la cabeza en posición neutra durante el empuje"]'::jsonb),

('20000000-0001-0000-0000-000000000014', 'es', 'Press militar tras nuca', 'press militar tras nuca behind the neck',
'["Sentate en un banco con respaldo y sostené la barra detrás de la cabeza a la altura de los hombros", "Mantené el agarre pronado un poco más ancho que el de los hombros y la espalda recta", "Empujá la barra hacia arriba hasta extender completamente los brazos sin perder la alineación cervical", "Descendé la barra con control detrás de la cabeza hasta la altura de las orejas o un poco más abajo", "No desciendas demasiado ni arquees la espalda; mantené tensión en los deltoides en todo momento"]'::jsonb),

('20000000-0001-0000-0000-000000000015', 'es', 'Press con kettlebell', 'press con kettlebell pesa rusa',
'["Parate con los pies al ancho de hombros y sostené la kettlebell a la altura del hombro en posición de rack", "Mantené el core firme y el codo apuntando ligeramente hacia adelante", "Empujá la kettlebell hacia arriba hasta extender completamente el brazo sobre la cabeza", "Descendé de forma controlada hasta la posición inicial sin dejar que el codo se abra hacia afuera", "Evitá arquear la espalda baja y mantené el abdomen contraído durante todo el movimiento"]'::jsonb),

('20000000-0001-0000-0000-000000000016', 'es', 'Elevaciones laterales en máquina', 'elevaciones laterales en maquina lateral raise machine',
'["Sentate en la máquina con los brazos apoyados contra los pads laterales", "Ajustá el asiento de modo que los codos estén ligeramente por debajo de los hombros", "Elevá los brazos lateralmente hasta que queden paralelos al suelo", "Bajá controladamente sin dejar que los pesos descansen entre repeticiones", "Mantené una ligera flexión de codos y evitá elevar los hombros hacia las orejas"]'::jsonb),

('20000000-0001-0000-0000-000000000017', 'es', 'Elevaciones frontales con disco', 'elevaciones frontales con disco plate front raise',
'["Parate derecho sosteniendo un disco con ambas manos frente a los muslos", "Con los brazos ligeramente flexionados, levantá el disco hacia el frente hasta la altura de los hombros", "Pausá un instante y bajá de forma controlada sin dejar que el disco toque el cuerpo", "Mantené el core activado y los hombros relajados durante todo el movimiento", "Evitá balancear el cuerpo o impulsarte con las piernas"]'::jsonb),

('20000000-0001-0000-0000-000000000018', 'es', 'Elevaciones laterales tumbado', 'elevaciones laterales tumbado lying lateral raise',
'["Acostate de lado sobre un banco o colchoneta sosteniendo una mancuerna con el brazo superior", "Mantené una ligera flexión en el codo y el brazo frente al cuerpo", "Levantá el brazo hacia arriba en un arco lateral hasta que quede perpendicular al suelo", "Bajá lentamente controlando la resistencia sin apoyar el brazo completamente", "Mantené el movimiento controlado y enfocá la tensión en el deltoide lateral"]'::jsonb),

('20000000-0001-0000-0000-000000000019', 'es', 'Face pull con banda', 'face pull con banda elastica resistance band',
'["Anclá una banda elástica a la altura del rostro y sujetá los extremos con ambas manos", "Con los brazos extendidos al frente, tirá de la banda hacia la cara separando las manos", "Llevá los codos hacia atrás y rotá externamente los hombros en el punto final", "Volvé lentamente a la posición inicial manteniendo tensión constante", "Evitá encoger los hombros y mantené el abdomen firme durante todo el movimiento"]'::jsonb),

('20000000-0001-0000-0000-000000000020', 'es', 'Press con banda elástica', 'press con banda elastica resistance band',
'["Anclá la banda detrás tuyo a la altura del pecho y sostené los extremos con ambas manos", "Colocá un pie adelante para estabilizarte y mantené el core activado", "Empujá los brazos hacia adelante hasta extenderlos completamente", "Volvé lentamente a la posición inicial controlando la resistencia de la banda", "No dejes que la banda te tire hacia atrás ni arquees la espalda baja"]'::jsonb),

('20000000-0001-0000-0000-000000000021', 'es', 'Elevaciones Y-T-W', 'elevaciones y t w ytw',
'["Acuestate boca abajo en un banco inclinado o usá un TRX en posición horizontal", "Con los brazos extendidos, levantá en forma de Y, luego de T y finalmente de W", "En cada forma, juntá los omóplatos y mantené la contracción por un segundo", "Bajá lentamente los brazos entre cada repetición sin perder la tensión", "Controlá el movimiento y mantené la mirada al piso con el cuello neutro"]'::jsonb),

('20000000-0001-0000-0000-000000000022', 'es', 'Press de hombros alternado', 'press de hombros alternado alternating',
'["Sentate o parate con una mancuerna en cada mano a la altura de los hombros", "Mantené el core firme y el pecho erguido durante todo el movimiento", "Empujá una mancuerna hacia arriba hasta extender completamente el brazo", "Bajá controladamente esa mancuerna mientras empujás la del otro lado", "Alterná de forma continua sin arquear la espalda ni usar impulso de piernas"]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('20000000-0001-0000-0000-000000000001', 'en', 'Barbell Overhead Press', 'barbell overhead press military',
'["Stand with feet shoulder-width apart and the bar resting on your upper chest.", "Grip the bar with an overhand grip, slightly wider than shoulder width.", "Engage your core and maintain slight scapular retraction.", "Push the bar up until your arms are fully extended without locking elbows.", "Lower the bar in a controlled manner to the starting position, maintaining tension.", "Avoid excessive lower back arching during the movement."]'::jsonb),

('20000000-0001-0000-0000-000000000002', 'en', 'Dumbbell Shoulder Press', 'dumbbell shoulder press overhead',
'["Sit on a bench with back support and feet firmly on the floor.", "Hold a dumbbell in each hand at shoulder height with overhand grip.", "Keep your back straight and shoulder blades retracted.", "Push the dumbbells up until your arms are fully extended.", "Lower the dumbbells in a controlled manner to starting height.", "Avoid bringing dumbbells together at top or arching your back during movement."]'::jsonb),

('20000000-0001-0000-0000-000000000003', 'en', 'Arnold Press', 'arnold press dumbbell',
'["Sit on a bench with back support holding a dumbbell in each hand in front of chest, palms facing you.", "Start the movement by rotating wrists outward while pushing dumbbells up.", "Finish with arms extended and palms facing forward.", "Lower in a controlled manner reversing the rotation back to starting position.", "Keep your core tight and avoid arching lower back."]'::jsonb),

('20000000-0001-0000-0000-000000000004', 'en', 'Dumbbell Lateral Raises', 'dumbbell lateral raises side',
'["Stand straight with a dumbbell in each hand at your sides, palms facing in.", "With slightly bent elbows, raise arms to the sides until parallel to the floor.", "Hold for a second at the top of the movement.", "Lower slowly to starting position without swinging.", "Avoid using momentum or shrugging shoulders."]'::jsonb),

('20000000-0001-0000-0000-000000000005', 'en', 'Dumbbell Front Raises', 'dumbbell front raises',
'["Stand holding a dumbbell in each hand in front of thighs with palms facing your body.", "Raise one arm to the front to shoulder height keeping elbow slightly bent.", "Lower in a controlled manner and repeat with the other arm or both at once.", "Avoid swinging or arching your back."]'::jsonb),

('20000000-0001-0000-0000-000000000006', 'en', 'Cable Lateral Raises', 'cable lateral raises side',
'["Stand beside the cable machine with cable set at the lowest position.", "Grab the handle with the far hand and keep arm slightly bent.", "Raise arm laterally to shoulder height maintaining movement control.", "Lower slowly to starting position.", "Avoid rotating torso or using momentum."]'::jsonb),

('20000000-0001-0000-0000-000000000007', 'en', 'Barbell Upright Row', 'barbell upright row',
'["Stand holding a barbell with overhand grip at shoulder width.", "Pull elbows up, bringing bar close to body up to chin height.", "Keep wrists straight and elbows higher than hands.", "Lower slowly to starting position.", "Avoid shrugging shoulders or using momentum."]'::jsonb),

('20000000-0001-0000-0000-000000000008', 'en', 'Cable Upright Row', 'cable upright row',
'["Attach a straight bar to low pulley and grip with overhand grip.", "Standing, pull bar up leading with elbows until it reaches near chin.", "Keep torso upright and core tight.", "Lower slowly until arms are fully extended.", "Avoid using momentum or raising shoulders."]'::jsonb),

('20000000-0001-0000-0000-000000000009', 'en', 'Rear Delt Fly with Dumbbells', 'rear delt fly dumbbells bent over',
'["With a dumbbell in each hand, lean torso forward until almost parallel to floor.", "Let arms hang with elbows slightly bent.", "Raise arms to sides to shoulder height, contracting rear delts.", "Lower slowly without losing tension.", "Avoid swinging or extending torso during movement."]'::jsonb),

('20000000-0001-0000-0000-000000000010', 'en', 'Cable Face Pull', 'cable face pull',
'["Set pulley at face height and attach a rope.", "Grip rope with neutral grip and step back to create tension.", "Pull rope toward your face separating hands at end of movement.", "Keep elbows high and shoulder blades retracted.", "Return slowly to starting position without losing tension."]'::jsonb),

('20000000-0001-0000-0000-000000000011', 'en', 'Machine Shoulder Press', 'machine shoulder press',
'["Adjust seat so handles are at shoulder height.", "Sit with back supported and feet firmly on floor.", "Grip handles with overhand grip.", "Push up until arms are fully extended.", "Lower in a controlled manner until handles are near shoulders.", "Avoid lifting back off the backrest."]'::jsonb),

('20000000-0001-0000-0000-000000000012', 'en', 'Scapular Push-Up', 'scapular push up plank',
'["Get in high plank position with hands under shoulders and body in straight line.", "Without bending elbows, push floor with hands to separate shoulder blades (scapular protraction).", "Hold position for a second, then let shoulder blades come back together (retraction).", "Repeat movement in controlled manner without losing body alignment.", "Avoid sinking lower back or bending elbows."]'::jsonb),

('20000000-0001-0000-0000-000000000013', 'en', 'Barbell Push Press', 'barbell push press',
'["Stand with feet shoulder-width apart holding bar at shoulder height with overhand grip", "Engage core and keep chest up with elbows slightly in front of bar", "Slightly bend knees and use small leg drive to initiate movement", "Extend hips and knees while pushing bar up until arms are fully extended", "Lower bar in controlled manner to starting position on shoulders and repeat", "Avoid arching back and keep head in neutral position during push"]'::jsonb),

('20000000-0001-0000-0000-000000000014', 'en', 'Behind the Neck Press', 'behind the neck press military',
'["Sit on bench with back support holding bar behind head at shoulder height", "Maintain overhand grip slightly wider than shoulders and straight back", "Push bar up until arms are fully extended without losing cervical alignment", "Lower bar with control behind head to ear level or slightly below", "Don''t descend too far or arch back; maintain deltoid tension throughout"]'::jsonb),

('20000000-0001-0000-0000-000000000015', 'en', 'Kettlebell Press', 'kettlebell press overhead',
'["Stand with feet shoulder-width apart holding kettlebell at shoulder in rack position", "Keep core tight and elbow pointing slightly forward", "Push kettlebell up until arm is fully extended overhead", "Lower in controlled manner to starting position without letting elbow flare out", "Avoid arching lower back and keep abs contracted throughout movement"]'::jsonb),

('20000000-0001-0000-0000-000000000016', 'en', 'Machine Lateral Raises', 'machine lateral raises side',
'["Sit on machine with arms against lateral pads", "Adjust seat so elbows are slightly below shoulders", "Raise arms laterally until parallel to floor", "Lower in controlled manner without letting weights rest between reps", "Maintain slight elbow bend and avoid raising shoulders toward ears"]'::jsonb),

('20000000-0001-0000-0000-000000000017', 'en', 'Plate Front Raises', 'plate front raises',
'["Stand straight holding a plate with both hands in front of thighs", "With slightly bent arms, raise plate to front to shoulder height", "Pause briefly and lower in controlled manner without letting plate touch body", "Keep core engaged and shoulders relaxed throughout movement", "Avoid swinging body or using leg drive"]'::jsonb),

('20000000-0001-0000-0000-000000000018', 'en', 'Lying Lateral Raises', 'lying lateral raises side',
'["Lie on side on bench or mat holding dumbbell with upper arm", "Maintain slight elbow bend with arm in front of body", "Raise arm up in lateral arc until perpendicular to floor", "Lower slowly controlling resistance without fully resting arm", "Keep movement controlled and focus tension on lateral deltoid"]'::jsonb),

('20000000-0001-0000-0000-000000000019', 'en', 'Resistance Band Face Pull', 'resistance band face pull',
'["Anchor band at face height and hold ends with both hands", "With arms extended forward, pull band toward face separating hands", "Bring elbows back and externally rotate shoulders at end point", "Return slowly to starting position maintaining constant tension", "Avoid shrugging shoulders and keep abs tight throughout movement"]'::jsonb),

('20000000-0001-0000-0000-000000000020', 'en', 'Resistance Band Press', 'resistance band press chest',
'["Anchor band behind you at chest height and hold ends with both hands", "Place one foot forward to stabilize and keep core engaged", "Push arms forward until fully extended", "Return slowly to starting position controlling band resistance", "Don''t let band pull you back or arch lower back"]'::jsonb),

('20000000-0001-0000-0000-000000000021', 'en', 'Y-T-W Raises', 'y t w raises ytw',
'["Lie face down on incline bench or use TRX in horizontal position", "With arms extended, raise in Y shape, then T shape, finally W shape", "In each shape, squeeze shoulder blades and hold contraction for a second", "Lower arms slowly between each rep without losing tension", "Control movement and keep gaze down with neutral neck"]'::jsonb),

('20000000-0001-0000-0000-000000000022', 'en', 'Alternating Shoulder Press', 'alternating shoulder press dumbbell',
'["Sit or stand with dumbbell in each hand at shoulder height", "Keep core tight and chest up throughout movement", "Push one dumbbell up until arm is fully extended", "Lower that dumbbell in controlled manner while pushing the other side", "Alternate continuously without arching back or using leg drive"]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '20000000-0001-%'
GROUP BY language_code;
