-- ============================================================================
-- TRADUCCIONES DE EJERCICIOS - GRUPO: TRÍCEPS
-- ============================================================================

-- ============================================================================
-- ESPAÑOL (es)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('30000000-0001-0000-0000-000000000001', 'es', 'Fondos en paralelas', 'fondos en paralelas dips',
'["Colocate en la estación de fondos con los brazos extendidos, sujetando cada barra a los costados del cuerpo.", "Mantené el pecho erguido y el core activado para evitar balanceos.", "Descendé flexionando los codos hasta que formen un ángulo de 90 grados o menor, según tu movilidad.", "Empujá con fuerza para volver a la posición inicial, extendiendo completamente los brazos.", "Evitá encoger los hombros y mantené la cabeza en posición neutra durante todo el movimiento."]'::jsonb),

('30000000-0001-0000-0000-000000000002', 'es', 'Extensiones de tríceps en polea alta', 'extensiones de triceps en polea alta pushdown',
'["Parate frente a la polea alta con una barra recta o en V conectada.", "Tomá la barra con agarre pronado (palmas hacia abajo) y mantené los codos pegados al cuerpo.", "Empujá la barra hacia abajo extendiendo completamente los brazos sin mover los codos.", "Volvé de forma controlada a la posición inicial sin dejar que el peso tire de vos.", "Mantené el core activado y no inclines el torso hacia adelante."]'::jsonb),

('30000000-0001-0000-0000-000000000003', 'es', 'Extensiones de tríceps con cuerda', 'extensiones de triceps con cuerda rope pushdown',
'["Conectá una cuerda a la polea alta y tomala con agarre neutro (palmas enfrentadas).", "Mantené los codos fijos a los costados del cuerpo.", "Empujá la cuerda hacia abajo separando los extremos al final del movimiento para mayor contracción.", "Volvé lentamente a la posición inicial sin perder tensión en el tríceps.", "Evitá balancear el cuerpo o mover los codos hacia adelante."]'::jsonb),

('30000000-0001-0000-0000-000000000004', 'es', 'Press francés con barra (Skull Crushers)', 'press frances con barra skull crushers',
'["Acostate en un banco plano sosteniendo una barra con agarre cerrado.", "Extendé los brazos por encima del pecho con los codos apuntando hacia el techo.", "Flexioná los codos para bajar la barra hacia la frente sin mover los brazos superiores.", "Extendé los brazos para volver a la posición inicial.", "Mantené los codos fijos y el core activado durante todo el movimiento."]'::jsonb),

('30000000-0001-0000-0000-000000000005', 'es', 'Press francés con mancuernas', 'press frances con mancuernas skull crushers dumbbells',
'["Acostate en un banco plano con una mancuerna en cada mano, brazos extendidos hacia el techo.", "Bajá las mancuernas flexionando los codos hasta que queden cerca de la cabeza.", "Mantené los codos apuntando hacia arriba sin abrirlos.", "Extendé los brazos para volver a la posición inicial.", "Evitá arquear la espalda y mantené el movimiento controlado."]'::jsonb),

('30000000-0001-0000-0000-000000000006', 'es', 'Extensiones de tríceps sobre la cabeza con mancuerna', 'extensiones de triceps sobre la cabeza con mancuerna overhead',
'["Sentate o parate sosteniendo una mancuerna con ambas manos detrás de la cabeza.", "Mantené los codos cerca de las orejas y el core activado.", "Extendé los brazos hacia arriba hasta estirar completamente el tríceps.", "Descendé de forma controlada sin abrir los codos.", "Evitá arquear la espalda durante el movimiento."]'::jsonb),

('30000000-0001-0000-0000-000000000007', 'es', 'Patada de tríceps (Kickbacks) con mancuerna', 'patada de triceps kickbacks con mancuerna',
'["Incliná el torso hacia adelante con una mancuerna en una mano y el codo pegado al cuerpo.", "Extendé el brazo hacia atrás hasta que quede paralelo al suelo.", "Contraé el tríceps al final del movimiento y mantené un segundo.", "Volvé a la posición inicial de forma controlada.", "Evitá balancear el brazo o mover el torso."]'::jsonb),

('30000000-0001-0000-0000-000000000008', 'es', 'Fondos en banco', 'fondos en banco bench dips',
'["Colocá las manos en el borde de un banco detrás tuyo, con los pies apoyados en el suelo o en otro banco.", "Mantené los codos apuntando hacia atrás y el pecho erguido.", "Descendé flexionando los codos hasta que los brazos formen un ángulo de 90 grados.", "Empujá hacia arriba para volver a la posición inicial sin bloquear los codos.", "No bajes demasiado para evitar lesiones en los hombros."]'::jsonb),

('30000000-0001-0000-0000-000000000009', 'es', 'Press cerrado con barra', 'press cerrado con barra close grip',
'["Acostate en un banco plano y tomá la barra con agarre cerrado, manos separadas al ancho de los hombros o menos.", "Bajá la barra controladamente hasta el pecho, manteniendo los codos cerca del cuerpo.", "Empujá la barra hacia arriba extendiendo completamente los brazos.", "Mantené las escápulas retraídas y el core activado durante todo el movimiento.", "Evitá rebotar la barra en el pecho."]'::jsonb),

('30000000-0001-0000-0000-000000000010', 'es', 'Extensiones de tríceps con agarre inverso en polea', 'extensiones de triceps con agarre inverso en polea reverse grip',
'["Parate frente a la polea alta con una barra recta conectada.", "Tomá la barra con agarre supino (palmas hacia arriba) y mantené los codos fijos al cuerpo.", "Empujá la barra hacia abajo extendiendo completamente los brazos.", "Volvé de forma controlada a la posición inicial.", "Este agarre enfatiza la cabeza lateral del tríceps."]'::jsonb),

('30000000-0001-0000-0000-000000000011', 'es', 'Extensión de tríceps unilateral en polea', 'extension de triceps unilateral en polea single arm',
'["Conectá un agarre de mano a la polea alta.", "De pie, tomá el agarre con una mano y mantené el codo fijo al costado.", "Empujá el agarre hacia abajo hasta extender completamente el brazo.", "Volvé lentamente a la posición inicial sin mover el codo.", "Repetí el mismo número de repeticiones con el otro brazo."]'::jsonb),

('30000000-0001-0000-0000-000000000012', 'es', 'Flexiones diamante', 'flexiones diamante diamond push ups',
'["Colocate en posición de flexión con las manos juntas formando un diamante debajo del pecho.", "Mantené el cuerpo en línea recta desde la cabeza hasta los pies.", "Descendé flexionando los codos hasta que el pecho casi toque las manos.", "Empujá hacia arriba hasta extender completamente los brazos.", "Mantené el core activado y evitá que las caderas suban o bajen."]'::jsonb),

('30000000-0001-0000-0000-000000000013', 'es', 'Extensión de tríceps en máquina', 'extension de triceps en maquina machine',
'["Ajustá el asiento para que los codos queden alineados con el eje de la máquina.", "Colocá los antebrazos contra las almohadillas y tomá las empuñaduras.", "Extendé los brazos empujando hasta la extensión completa.", "Volvé lentamente a la posición inicial sin dejar que el peso descanse.", "Mantené los hombros relajados durante todo el movimiento."]'::jsonb),

('30000000-0001-0000-0000-000000000014', 'es', 'Press francés con barra EZ', 'press frances con barra ez skull crushers ez bar',
'["Acostate en un banco plano sosteniendo una barra EZ con agarre cerrado.", "Extendé los brazos por encima del pecho.", "Bajá la barra hacia la frente flexionando solo los codos.", "Extendé los brazos para volver a la posición inicial.", "El agarre de la barra EZ reduce la tensión en las muñecas."]'::jsonb),

('30000000-0001-0000-0000-000000000015', 'es', 'Extensión de tríceps sobre la cabeza con polea', 'extension de triceps sobre la cabeza con polea overhead cable',
'["Conectá una cuerda a la polea baja y dá la espalda a la máquina.", "Tomá la cuerda con ambas manos y llevala detrás de la cabeza con los codos flexionados.", "Dá un paso adelante para generar tensión y estabilizate con un pie adelante.", "Extendé los brazos hacia adelante y arriba hasta estirar completamente los tríceps.", "Volvé de forma controlada sin mover los codos de su posición."]'::jsonb),

('30000000-0001-0000-0000-000000000016', 'es', 'JM Press', 'jm press barra',
'["Acostate en un banco plano con una barra tomada con agarre cerrado.", "Bajá la barra en línea recta hacia la parte superior del pecho/mentón manteniendo los codos hacia adelante.", "Mantené los codos apuntando hacia arriba mientras baja la barra.", "Extendé los brazos empujando la barra hacia arriba.", "Este ejercicio combina elementos del press cerrado y el skull crusher."]'::jsonb),

('30000000-0001-0000-0000-000000000017', 'es', 'Extensión de tríceps tumbado con mancuerna unilateral', 'extension de triceps tumbado con mancuerna unilateral lying',
'["Acostate en un banco plano sosteniendo una mancuerna con una mano, brazo extendido.", "Bajá la mancuerna flexionando el codo hasta que llegue cerca de la oreja del mismo lado.", "Mantené el brazo superior vertical durante todo el movimiento.", "Extendé el brazo para volver a la posición inicial.", "Repetí con el otro brazo después de completar las repeticiones."]'::jsonb),

('30000000-0001-0000-0000-000000000018', 'es', 'Extensión de tríceps con banda elástica', 'extension de triceps con banda elastica resistance band',
'["Pisá una banda elástica con los pies o sujetala detrás de la espalda.", "Tomá el otro extremo de la banda con las manos detrás de la cabeza.", "Extendé los brazos hacia arriba estirando la banda completamente.", "Volvé de forma controlada a la posición inicial.", "Mantené los codos cerca de las orejas durante todo el movimiento."]'::jsonb),

('30000000-0001-0000-0000-000000000019', 'es', 'Patada de tríceps en polea', 'patada de triceps en polea cable kickback',
'["Conectá un agarre de mano a la polea baja y parate de espaldas a la máquina.", "Incliná el torso hacia adelante y mantené el codo fijo al costado del cuerpo.", "Extendé el brazo hacia atrás hasta que quede paralelo al suelo.", "Contraé el tríceps al final y volvé de forma controlada.", "Evitá mover el codo o balancear el cuerpo."]'::jsonb),

('30000000-0001-0000-0000-000000000020', 'es', 'Flexiones cerradas', 'flexiones cerradas close grip push ups',
'["Colocate en posición de flexión con las manos separadas al ancho de los hombros o menos.", "Mantené los codos cerca del cuerpo durante el descenso.", "Bajá hasta que el pecho casi toque el suelo.", "Empujá hacia arriba extendiendo completamente los brazos.", "Mantené el core activado y el cuerpo en línea recta."]'::jsonb);

-- ============================================================================
-- ENGLISH (en)
-- ============================================================================

INSERT INTO exercise_translations (exercise_id, language_code, name, name_search, instructions) VALUES

('30000000-0001-0000-0000-000000000001', 'en', 'Parallel Bar Dips', 'parallel bar dips',
'["Position yourself on the dip station with arms extended, gripping each bar at your sides.", "Keep chest up and core engaged to avoid swinging.", "Descend by bending elbows until they form a 90-degree angle or less, depending on your mobility.", "Push forcefully to return to starting position, fully extending arms.", "Avoid shrugging shoulders and keep head in neutral position throughout movement."]'::jsonb),

('30000000-0001-0000-0000-000000000002', 'en', 'Cable Tricep Pushdown', 'cable tricep pushdown',
'["Stand facing high pulley with straight or V-bar attached.", "Grip bar with overhand grip (palms down) and keep elbows close to body.", "Push bar down fully extending arms without moving elbows.", "Return in controlled manner to starting position without letting weight pull you.", "Keep core engaged and don''t lean torso forward."]'::jsonb),

('30000000-0001-0000-0000-000000000003', 'en', 'Rope Tricep Pushdown', 'rope tricep pushdown cable',
'["Attach rope to high pulley and grip with neutral grip (palms facing).", "Keep elbows fixed at your sides.", "Push rope down separating ends at bottom of movement for greater contraction.", "Return slowly to starting position without losing tricep tension.", "Avoid swinging body or moving elbows forward."]'::jsonb),

('30000000-0001-0000-0000-000000000004', 'en', 'Barbell Skull Crushers', 'barbell skull crushers french press lying tricep extension',
'["Lie on flat bench holding barbell with close grip.", "Extend arms above chest with elbows pointing toward ceiling.", "Bend elbows to lower bar toward forehead without moving upper arms.", "Extend arms to return to starting position.", "Keep elbows fixed and core engaged throughout movement."]'::jsonb),

('30000000-0001-0000-0000-000000000005', 'en', 'Dumbbell Skull Crushers', 'dumbbell skull crushers french press',
'["Lie on flat bench with dumbbell in each hand, arms extended toward ceiling.", "Lower dumbbells by bending elbows until they''re near your head.", "Keep elbows pointing up without letting them flare.", "Extend arms to return to starting position.", "Avoid arching back and keep movement controlled."]'::jsonb),

('30000000-0001-0000-0000-000000000006', 'en', 'Overhead Dumbbell Tricep Extension', 'overhead dumbbell tricep extension',
'["Sit or stand holding dumbbell with both hands behind head.", "Keep elbows close to ears and core engaged.", "Extend arms upward until triceps are fully stretched.", "Descend in controlled manner without opening elbows.", "Avoid arching back during movement."]'::jsonb),

('30000000-0001-0000-0000-000000000007', 'en', 'Dumbbell Tricep Kickbacks', 'dumbbell tricep kickbacks',
'["Lean torso forward with dumbbell in one hand and elbow close to body.", "Extend arm backward until it''s parallel to floor.", "Contract tricep at end of movement and hold for a second.", "Return to starting position in controlled manner.", "Avoid swinging arm or moving torso."]'::jsonb),

('30000000-0001-0000-0000-000000000008', 'en', 'Bench Dips', 'bench dips',
'["Place hands on edge of bench behind you, with feet on floor or another bench.", "Keep elbows pointing back and chest up.", "Descend by bending elbows until arms form 90-degree angle.", "Push up to return to starting position without locking elbows.", "Don''t go too low to avoid shoulder injuries."]'::jsonb),

('30000000-0001-0000-0000-000000000009', 'en', 'Close Grip Bench Press', 'close grip bench press',
'["Lie on flat bench and grip bar with close grip, hands shoulder-width apart or less.", "Lower bar in controlled manner to chest, keeping elbows close to body.", "Push bar up fully extending arms.", "Keep shoulder blades retracted and core engaged throughout movement.", "Avoid bouncing bar off chest."]'::jsonb),

('30000000-0001-0000-0000-000000000010', 'en', 'Reverse Grip Tricep Pushdown', 'reverse grip tricep pushdown cable',
'["Stand facing high pulley with straight bar attached.", "Grip bar with supinated grip (palms up) and keep elbows fixed to body.", "Push bar down fully extending arms.", "Return in controlled manner to starting position.", "This grip emphasizes lateral head of tricep."]'::jsonb),

('30000000-0001-0000-0000-000000000011', 'en', 'Single Arm Cable Tricep Extension', 'single arm cable tricep extension',
'["Attach handle to high pulley.", "Standing, grip handle with one hand and keep elbow fixed at side.", "Push handle down until arm is fully extended.", "Return slowly to starting position without moving elbow.", "Repeat same number of reps with other arm."]'::jsonb),

('30000000-0001-0000-0000-000000000012', 'en', 'Diamond Push-Ups', 'diamond push ups',
'["Get in push-up position with hands together forming diamond shape under chest.", "Keep body in straight line from head to feet.", "Descend by bending elbows until chest nearly touches hands.", "Push up until arms are fully extended.", "Keep core engaged and avoid letting hips rise or drop."]'::jsonb),

('30000000-0001-0000-0000-000000000013', 'en', 'Machine Tricep Extension', 'machine tricep extension',
'["Adjust seat so elbows align with machine''s axis.", "Place forearms against pads and grip handles.", "Extend arms pushing to full extension.", "Return slowly to starting position without letting weight rest.", "Keep shoulders relaxed throughout movement."]'::jsonb),

('30000000-0001-0000-0000-000000000014', 'en', 'EZ Bar Skull Crushers', 'ez bar skull crushers french press',
'["Lie on flat bench holding EZ bar with close grip.", "Extend arms above chest.", "Lower bar toward forehead bending only elbows.", "Extend arms to return to starting position.", "EZ bar grip reduces wrist strain."]'::jsonb),

('30000000-0001-0000-0000-000000000015', 'en', 'Overhead Cable Tricep Extension', 'overhead cable tricep extension',
'["Attach rope to low pulley and face away from machine.", "Grip rope with both hands and bring it behind head with elbows bent.", "Step forward to create tension and stabilize with one foot forward.", "Extend arms forward and up until triceps are fully stretched.", "Return in controlled manner without moving elbows from position."]'::jsonb),

('30000000-0001-0000-0000-000000000016', 'en', 'JM Press', 'jm press barbell',
'["Lie on flat bench with bar gripped close.", "Lower bar in straight line toward upper chest/chin keeping elbows forward.", "Keep elbows pointing up as bar descends.", "Extend arms pushing bar up.", "This exercise combines elements of close grip press and skull crusher."]'::jsonb),

('30000000-0001-0000-0000-000000000017', 'en', 'Lying Single Arm Dumbbell Tricep Extension', 'lying single arm dumbbell tricep extension',
'["Lie on flat bench holding dumbbell with one hand, arm extended.", "Lower dumbbell by bending elbow until it reaches near same-side ear.", "Keep upper arm vertical throughout movement.", "Extend arm to return to starting position.", "Repeat with other arm after completing reps."]'::jsonb),

('30000000-0001-0000-0000-000000000018', 'en', 'Resistance Band Tricep Extension', 'resistance band tricep extension overhead',
'["Step on band with feet or secure it behind back.", "Grip other end of band with hands behind head.", "Extend arms upward fully stretching band.", "Return in controlled manner to starting position.", "Keep elbows close to ears throughout movement."]'::jsonb),

('30000000-0001-0000-0000-000000000019', 'en', 'Cable Tricep Kickback', 'cable tricep kickback',
'["Attach handle to low pulley and stand facing away from machine.", "Lean torso forward and keep elbow fixed at side of body.", "Extend arm backward until parallel to floor.", "Contract tricep at end and return in controlled manner.", "Avoid moving elbow or swinging body."]'::jsonb),

('30000000-0001-0000-0000-000000000020', 'en', 'Close Grip Push-Ups', 'close grip push ups narrow',
'["Get in push-up position with hands shoulder-width apart or less.", "Keep elbows close to body during descent.", "Lower until chest nearly touches floor.", "Push up fully extending arms.", "Keep core engaged and body in straight line."]'::jsonb);

-- Verificar la inserción
SELECT language_code, COUNT(*) as total 
FROM exercise_translations 
WHERE exercise_id::text LIKE '30000000-0001-%'
GROUP BY language_code;
